article div ul li:hover a,
article div ul li:hover a:visited,
article div ul li:hover a:hover { color:#fff !important }
article div ul li:hover:before { color:#fafafa !important }

article div h2 { border-bottom:1px solid #eee !important; color:#fff !important; line-height:35px !important; margin:0 !important; padding:0 !important; text-indent:18px !important }
article div ul { margin:0 !important }

#front-page-newest h2,
#front-page-best h2 {
border-bottom:1px solid #eee !important;
border-top:1px solid #eee !important;
color:#0469A3 !important }

}

@-moz-document url-prefix("http://userstyles.org/styles/browse"),
               url-prefix("http://userstyles.org/categories") , url-prefix("https://userstyles.org/styles/browse"), url-prefix("https://userstyles.org/categories") {

article form { border-bottom:1px solid #eee !important; padding:0 !important }

form p { text-align:center !important }
form p input,
form p select {
-moz-appearance:none !important;
background: #ffffff !important;
border:1px solid #eee !important;
color:#45484d !important;
}

.style-brief {
background-color:#eaeaea !important;
border-bottom:1px solid #ccc;
padding:10px !important;
margin:0 !important }
.style-brief:nth-child(2n+1) { background-color:#e5e5e5 !important }

.style-brief p { font-size:10px !important }

.pagination {
clear:left !important;
padding:1em 0 0 !important;
text-align:center !important }

.pagination span,
.pagination a {
background-color:#f4f4f4 !important;
display:inline-block !important;
line-height:40px !important;
overflow:hidden !important;
padding:0 !important;
text-align:center !important;
width:58px !important }

.pagination a,
.pagination a:visited { color:#777 !important }

.pagination a:first-child { width:7% !important }
.pagination .next_page,
.pagination .prev_page { text-indent:-999px !important }
.pagination .disabled { opacity:0.3 !important }
.pagination .current { background-color:#0469A3 !important; color:#fff !important }

#style-list { border-left:1px solid #eee !important; padding:0 0 0 12px !important }

#style-list li {
border-bottom:1px solid #eee !important;
height:25px !important;
