list-style:none !important;
padding:2px 3px 2px 18px !important;
}

#style-list li:last-child { border-bottom:none !important }
#style-list li:before {
content:"total: " attr(total-install-count) " week: " attr(weekly-install-count) " Average-Rating: " attr(average-rating) !important;
color:#bbb !important;
font-size:11px !important;
float:right !important;
padding:0 3px !important;
text-align:left !important;
width:290px !important }

#style-list li a { display:block }
#style-list li a:after { content: " (Visited)"; color:transparent !important; font-style:italic !important }
#style-list li:nth-child(2n+2) a:after { color:#f5f5f5 !important }
#style-list li a:visited { color:#999 !important }
#style-list li a:hover:visited { color:#555 !important }
#style-list li a:visited:after { color:#ddd }

#style-list li:hover a:after { color:#4D97C0 !important }
#style-list li:hover a:visited:after { color:#ff0 !important }

#style-list li:hover a,
#style-list li:hover a:visited,
#style-list li:hover a:hover { color:#fff !important }
#style-list li:hover:before { color:#fafafa !important }

}


@-moz-document url-prefix("http://userstyles.org/styles/") , url-prefix("https://userstyles.org/styles/") {

