#before-screenshot:hover figcaption,
#after-screenshot:hover figcaption { opacity:0.6 }

article p { line-height:1.6em !important; margin:1em 0 1em !important }

article h2 { color:#666 !important; font-style:normal !important }

article { position:relative }

#social iframe { width:250px !important }

