#install {
background-color:transparent !important;
border:0 !important;
margin:1em 0 !important;
padding:0 !important;
}

#related h2,
article section h2 { font-size:16px !important; padding-left:30px !important }

#install p { margin:0 !important; padding:5px 10px !important }
#install p,
#install p a {  color:#fff !important }

#install button {
background-color:green !important;
