css:
@namespace url(http://www.w3.org/1999/xhtml);

/* START - - - Apply to entire domain - - - START */
@-moz-document domain("forums.eveonline.com") {

/* Remove background image and set default background color*/
body { background: #000000 !important; }

/* Force overall forum width */
.colmaskConstrained { width: 95% !important; min-width:966px !important; }

/* Attributes for section containing "eve date and time" */
#sectionHeaderContainer { width: 95% !important; min-width:966px !important; }

/* Attributes for text "eve time" */
#timeDateContainer { color: #2C2C38 !important; }

/* Attributes for item "date and time" */
span#jclock { font-size: 11px !important; }

/* "ToTop"-button position */
#toTop { left: 15px !important; margin-left: 0px !important; }

/* Remove topic tooltip */
.forum-tooltip { display: none !important; }

/* Uncomment following topic tooltip code if you enable the functionality */
/* .forum-tooltip { background: #4D4D57 !important; padding: 5px !important; border: 1px solid #666666 !important; } /*

/* Frontpage forum selector layout */
.toggle { width: 100% !important; }
.trigger { background: #9F6C17 !important; border: 1px solid #FA9E0E !important; width: 40% !important; }
.expand { margin: 3px !important; width: 99% !important; background: #2C2C38 !important; padding: 1px !important; border: 1px solid #666666 !important;}
.forum-wrapper { margin-top: 5px !important; width: 97% !important; }
.toggle-content { margin-top: 5px !important; width: 100% !important; background-color: #000000 !important; }

/* Global avatar layout and user info format tweaks */
div.avatar { width: auto !important; background: transparent !important; padding: 0px !important; margin: 0 0 0 4px !important; text-align: left !important; }
div.badge { top: -5px !important; left: -25px !important; }
div.section { padding: 0 0 0 4px !important; font-family: Verdana !important; font-size: 11px !important; }
.corp a { font-weight: normal !important; }
span.alliance { font-weight: bold !important; font-style: normal !important; color: white !important; }

/* Global forum border */
.yafnet { border: 0px solid #666666 !important; }

/* Forum path background */
.yafPageLink { background: #000000 !important; padding: 5px !important; border-top: 1px solid #666666 !important; border-left: 1px solid #666666 !important; border-bottom: 1px solid #666666 !important; border-right: 1px solid #666666 !important; }

/* Forum label layout and colors */
.for-head { background: #4D4D57 !important; padding: 3px !important; border-top: 1px solid #666666 !important; border-left: 1px solid #666666 !important; border-bottom: 1px solid #666666 !important; border-right: 1px solid #666666 !important; height: 20px !important; overflow: hidden !important;}

/* Forum rules and archives layout and colors */
table.content { width: 100% !important; }
.support-nav { background: #4D4D57 !important; padding: 0px !important; border: 1px solid #666666 !important; }
td.post { background: #2C2C38 !important; padding: 5px !important;  }

/* Forum subpage linker layout and colors */
.command { background: #4D4D57 !important; border: 1px solid #666666 !important; padding-top: 1px !important; margin-bottom: 1px !important; margin-top: 1px !important; }
.pagecount { background: #37353A !important; }
.gotoHeader { background: #37353A !important; }

/* Field labels */
.header2 {padding: 5px !important;}

/* Forum title layout and colors */
.topicTitle { background: #4D4D57 !important; border: 1px solid #666666 !important; }
.postTitle { background: #4D4D57 !important; border-bottom: 1px solid #666666 !important; }

/* Topic and forum browser layout and colors */
.topicSubTitle { background: #2C2C38 !important; padding: 5px !important; border: 1px solid #666666 !important; }
.topicMain { font-weight: normal !important; background: #4D4D57 !important; padding: 5px !important; border: 1px solid #666666 !important; }
.topic_new { font-weight: bold !important; }
.sticky { font-weight: normal !important; background: #2C2C38 !important; padding: 3px !important; border: 1px solid #666666 !important; }
.sticky_new { font-weight: bold !important; background: #2C2C38 !important; padding: 3px !important; border: 1px solid #FA9E0E !important; }
.announcement { font-weight: normal !important; background: #2C2C38 !important; padding: 3px !important; border: 1px solid #666666 !important; }
.announcement_new { font-weight: bold !important; background: #2C2C38 !important; padding: 3px !important; border: 1px solid #FA9E0E !important; }
.topicAuthor { background: #2C2C38 !important; padding: 5px !important; border: 1px solid #666666 !important; }
.topicRow { background: #4D4D57 !important; padding: 5px !important; border: 1px solid #666666 !important; }
.topicReplies { background: #4D4D57 !important; padding: 5px !important; border: 1px solid #666666 !important; }
.topicViews { background: #4D4D57 !important; padding: 5px !important; border: 1px solid #666666 !important; }
.topicLastPost { background: #2C2C38 !important; padding: 5px !important; border: 1px solid #666666 !important; }

/* Search area layout and colors */
.main-box { background: #4D4D57 !important; border: 1px solid #666666 !important; }
 
/* Following lines control "likes"-related comments and buttons. */
   .likes { display: none !important; } 
/* .like-wrapper { display: none !important; } */
/* .like-count-wrapper { display: none !important; } */

}
/* END - - - Apply to entire domain - - - END */



/* START - - - Apply only to posts writing page - - - START */
@-moz-document url-prefix("https://forums.eveonline.com/default.aspx?g=postmessage") {

/* Heading layout and colors */
.head { background: #4D4D57 !important; padding-top: 5px !important; padding-bottom: 5px !important; border-bottom: 1px solid #666666 !important; }
.author { background: #2C2C38 !important; padding-top: 5px !important; padding-bottom: 0px !important; border-bottom: 1px solid #666666 !important; }

/* Edit box layout and colors */
.main { width: 80% !important; margin: 7px !important; background: #4D4D57 !important; }
.clearfix { width: 100% !important; background-color: #000000 !important; margin: 0px !important; margin-bottom: 0px !important; }
.BBCodeEditor { background-color: #2C2C38 !important; }
.pm-wrapper { background-color: #2C2C38 !important; border: 1px solid #666666 !important; }
.left { background: #2C2C38 !important; }
.line { padding: 0px !important; }
.title { padding: 5px !important; }
.right { margin-right: 5px !important; padding: 5px !important; border-top: 1px solid #666666 !important; border-left: 1px solid #666666 !important; border-bottom: 1px solid #666666 !important; border-right: 1px solid #666666 !important; }
img.avatar-eve { width:  64px !important; height: 64px !important; }

/* Color and layout of "Last 10 Posts view" section */
.auth { background-color: #2C2C38 !important; min-width: 154px !important; padding: 5px !important; border-left: 0px !important; border-bottom: 0px !important; border-right: 0px !important}
.topic { background-color: #2C2C38 !important; width: 100% !important; padding: 5px !important; border-right: 0px solid !important; border-bottom: 0px !important; }
.postcontainer { background-color: #4D4D57 !important; margin-bottom: 0px !important; }
.postheader { border-bottom: 1px solid #666666 !important; border-top: 1px solid #666666 !important; background-color: #4D4D57 !important;}
.date-info { border-bottom: 1px solid #808080 !important; }
.yafsignature { border-top: 1px solid #808080 !important; }
.message { font-family: Verdana !important; font-size: 11px !important; min-height: 0px !important; background: #2C2C38 !important; padding: 5px !important; }
tr.post { background: #2C2C38 !important; border: 0px !important; }

}
/* END - - - Apply only to posts writing page - - - END */



/* START - - - Apply only to posts reading page - - - START */

@-moz-document url-prefix("https://forums.eveonline.com/default.aspx?g=posts") {
/* Column title layout */
.auth { background-color: #2C2C38 !important; min-width: 138px !important; padding: 5px !important; border: 0px !important; }
.topic { background-color: #2C2C38 !important; width: 100% !important; padding: 5px !important; }
.header1 { background: #2C2C38 !important; padding: 0px !important; }
.title-line { background: #4D4D57 !important; padding: 5px !important; border-bottom: 1px solid #666666 !important;  border-top: 1px solid #666666 !important;}

/* Forcing full width for post containers */
.postdiv { width: 100% !important; }
.postedLeft { width: 100% !important; }
.postPosted { width: 100% !important; }

/* Post container colors and layout */
.postContainer { background-color: #4D4D57 !important; margin-bottom: 0px !important; }
.postContainer_Alt { background-color:  #2C2C38 !important; margin-bottom: 0px !important; }
.postHeader { border: 1px solid #666666 !important; }
.postPosted { padding: 5px !important; border-bottom: 1px solid #666666 !important; border-right: 1px solid #666666 !important; }
.postedLeft { border-bottom: 1px solid #808080 !important; }
div.postdiv { font-family: Verdana !important; font-size: 11px !important; min-height: 0px !important; }
.yafsignature { border-top: 1px solid #808080 !important; }
.UserBox { padding: 4px !important; text-align: left !important; border-right: 1px solid #666666 !important; border-left: 1px solid #666666 !important; border-bottom: 1px solid #666666 !important; }
img.avatarimage { width:  64px !important; height: 64px !important; }

/* Full width Quick reply feature layout and colors (will move quick jump to bottom of the page) */

.author { background: #2C2C38 !important; padding-top: 5px !important; padding-bottom: 0px !important; border-bottom: 1px solid #666666 !important; }
.quick { width: 100% !important; }
.trigger { width: 590px !important; background: #9F6C17 !important; border: 1px solid #FA9E0E !important;}
.toggle-content { margin: 5px !important; width: 99% !important; background-color: #2C2C38 !important; }
.main { width: 81% !important; background: #4D4D57 !important; margin: 5px !important; }
.clearfix { width: 100% !important; background-color: #000000 !important; margin: 0px !important; border-right: 0px !important; }
.quick-reply { width: 98% !important; background: #4d4D57 !important; }
.content { width: 98% !important; }
.basicBBCodeEditor { width: 98% !important;  background-color: #2C2C38 !important; }
.reply-buttons { margin-right: 18px !important; background-color: #4d4D57 !important; }
.expand { margin: 5px !important; width: 593px !important; background: #2C2C38 !important; padding: 1px !important; border: 1px solid #666666 !important;}
.pm-wrapper { background-color: #2C2C38 !important; }
.left { width: 17% !important; background: #2C2C38 !important; }
.title { padding: 5px !important; }
img.avatar-eve { width:  64px !important; height: 64px !important; }

/* Optional feature - if you enable this, disable the previous block */
/* Compact quick reply (will not move quick jump to bottom of the page) */
