/* Ignored posts colors and layout */
.inner-hidden { width: 98% !important; background: #330000 !important; margin-top: 1px !important; margin-bottom: 1px !important; border: 1px solid #000000 !important; }

}
/* END - - - Apply only to posts reading page - - - END */



/* START - - - Apply only to favorites page - - - START */
@-moz-document url-prefix("https://forums.eveonline.com/default.aspx?g=mytopics") {

/* Forum title layout and colors (exception) */
.topicTitle { background: #9F6C17 !important; border: 1px solid #FA9E0E !important; }

/* Force scalable width */
.fav-wrapper { width: 99% !important; padding: 2px !important; }
.ui-corner-all { width: 98% !important; padding-top: 5px !important; padding-bottom: 0px !important;}
.ui-widget-content { width: 98% !important; }
.ui-tabs { width: 100% !important; }
.ui-corner-bottom { padding-left: 11px !important; padding-right: 0px !important;}

/* Tab selector colors */
.ui-corner-all { background: #3C3C3C !important; }
.ui-tabs-nav { border-color: #3C3C3C !important; }
.ui-state-default { border-color: #3C3C3C !important; }
.ui-state-default a { background: #474747 !important; color: #666666 !important;}
.ui-tabs-selected { border-color: #666666 !important; }
.ui-tabs-selected a { background: #282828 !important; color: #FFFFFF !important;}
.ui-state-hover { border-color: #666666 !important; }

}
/* END - - - Apply only to favorites page - - - END */



/* START - - - Apply only to report posts page - - - START */
@-moz-document url-prefix("https://forums.eveonline.com/default.aspx?g=reportpost") {

/* Post container width */
div.post{ width: 95% !important; }

/* Heading layout and colors */
.head { background: #4D4D57 !important; padding-top: 5px !important; padding-bottom: 5px !important; border-bottom: 1px solid #666666 !important; }
.author { background: #2C2C38 !important; padding-top: 5px !important; padding-bottom: 0px !important; border-bottom: 1px solid #666666 !important; }

/* Edit box layout and colors */
.main { width: 80% !important; margin: 7px !important; background: #4D4D57 !important; }
.clearfix { width: 100% !important; background-color: #000000 !important; margin: 0px !important; margin-bottom: 0px !important;}
.BBCodeEditor { background-color: #2C2C38 !important; }
.pm-wrapper { background-color: #2C2C38 !important; border: 1px solid #666666 !important;}
.left { background: #2C2C38 !important; }
.line { padding: 0px !important; }
.title { padding: 5px !important; }
.right { margin-right: 5px !important; padding: 5px !important; border-top: 1px solid #666666 !important; border-left: 1px solid #666666 !important; border-bottom: 1px solid #666666 !important; border-right: 1px solid #666666 !important; }
img.avatar-eve { width:  64px !important; height: 64px !important; }

}
/* END - - - Apply only to report posts page - - - END */
