css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lawblog.de") {


/*** Erstmal die Seitenbreite anpassen ***/
html, body{ margin:0; padding:0; background:black; } /*** ***/

#page.hfeed{
    float:left;
    margin:1em;
    background-color:#333;
}

#content{ background-color:#333; }
.singular #content{ margin:1em; padding:1em; }

/******** Die Header-Größe (samt BG-Grafik) ********/
/* erstmal den Header wieder in den Dokumentenfluß */
#branding hgroup{
    position:relative;
    border:0;
    background-color:#222;
}
/* Diese merkwürdige Grafik ausblenden */
#branding a img{ display:none; }

/* Navigationszeile anpassen */
nav{
    background-color:#333;
}
nav#nav-single, ul#menu-hauptmenue li a{
    font-family:Verdana, Arial, Helvetica, sans-serif;
    color:#ccf!important;
}


/* Datumsangabe anpassen */
div.entry-meta span a{
    font-family:Verdana, Arial, Helvetica, sans-serif;
    font-style:normal;
    font-size:.8em;
    color:#ddd;
}

/* Titelzeile auf Seitenbreite */
.singular .entry-header{
    margin:1em;
    width:100%;
}

/* Titelzeile Schrift anpassen */
header.entry-header{ margin:1em 0 !important;}
h1.entry-title{
    font-family:"Trebuchet MS", Arial, Helvetica, sans-serif;
    font-style:italic;
    font-size:2em;
    color:#ddd!important;
}

/* Beitragsbreite anpassen */
.singular .entry-content {width:auto; padding:0; margin:0; }

/* Beitragstext anpassen */
.entry-content p{
    font-family:"Noto Serif Light", Garamond, "Times New Roman", serif !important;
    font-size:1.1em;
    color:#eee;
}





}
