css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.eveonline.com"), domain("testforums.evegate.com") {

#mainNavContainer {
 width: auto !important;
}

#logoContainer {
 left: 3% !important;
}

#headerContainer {
 width: auto !important;
 margin-right: 20px !important;
}

#logOn {
 margin-right: 1% !important;
}

body.forum-body {
 background-image: none !important;
}

.colmaskConstrained {
 width: 100% !important;
}

#sectionHeaderContainer {
 width: 100% !important;
}

/*Time right to the breadcrumb*/
#timeDateContainer {
 margin-right: 1% !important;
}

.tabs-forum {
 left: 0 !important;
}

.tabs-forum ul {
 margin-left: 15px !important;
}

.yafnet table.content td {
 padding: 5px 5px 5px 5px !important;
 text-align: center !important;
 vertical-align: middle !important;
}

/*Post a reply */

.yafnet table.content td.message {
 text-align: left !important;
}

.yafnet table.content .date-info {
 text-align: left !important;
}

.yafnet textarea {
 font-size: 12pt !important;
}

.pm-wrapper > .left {
 margin-top: 24px !important;
 position: absolute !important;
}

.pm-wrapper > .main {
 margin-left: 160px !important;
 width: auto !important;
}

/*END Post a reply */

/*This is for default.aspx?g=forum&c=61*/
.forum-wrapper {
 width: 98% !important;
}

/*Hide the text of Normal/Compact views*/
.view-options a {
 text-indent: -5000px !important;
}

/*Alignment of the numbers in forum/thread lists*/

.yafnet table.content.topics tr.topicSubTitle > td:first-child {
 text-align: left !important;
 padding-left: 35px !important;
}

.yafnet table.content.topics tr.topicSubTitle td:nth-child(2) {
 text-align: left !important;
 padding-left: 5px !important;
}

.yafnet table.content.topics tr.topicSubTitle td:nth-child(6) {
 text-align: left !important;
 padding-left: 5px !important;
}

.yafnet table.content.topics.frontpage tr.topicSubTitle > td:first-child {
 text-align: left !important;
 padding-left: 15px !important;
}

.yafnet table.content.topics.frontpage tr.topicSubTitle td:nth-child(2) {
 text-align: center !important;
 padding-left: 5px !important;
}

.yafnet table.content.topics.frontpage tr.topicSubTitle td:nth-child(3) {
 text-align: left !important;
 padding-left: 5px !important;
}

.yafnet table.content.topics td.topicMain {
 padding-left: 20px !important;
}

/*To allow vertical centering (remove absolute from .topicPager or it will break)*/
.yafnet .topicMain a {
 height: auto !important;
}

.yafnet .topicPager {
 position: relative !important;
}

.forum-tooltip {
 left: 60% !important;
}

.yafnet table.content.topics td.topicAuthor {
 text-align: left !important;
 padding-left: 5px !important;
}

.yafnet table.content.topics td.topicLastPost {
 text-align: left !important;
 padding-left: 5px !important;
}

.yafnet table.content td.topic {
 width: auto !important;
}

.yafnet tr.topicRow .topicMain, .yafnet tr.topicRow_Alt .topicMain {
 text-align: left !important;
}

/*Posts*/

/*Top of the table*/
.yafnet tr.postpreheader {
 background-size: auto 100% !important;
}

/*Avatar*/
.yafnet table.content tr.postheader td.UserBox {
 vertical-align: top !important;
}

/*Under the avatar*/
.yafnet .section {
 color: white !important;
}

/*Up to 999999 likes in a single line*/
.userbox .likes {
 font-size: 13px !important;
 color: #CFCFCF !important;
 margin-top: 6px !important;
}

/*Whole post*/
.yafnet table.content tr.postheader td.postPosted {
 vertical-align: top !important;
 width: 100% !important;
 padding-bottom: 0 !important;
 padding-top: 0 !important;
}

/*Post itself*/
.yafnet div.postdiv {
 font-size: 12pt !important;
 width: 100% !important;
 color: #EFEFEF !important;
}

.postContainer {
 background-color: #353535 !important;
}

.postContainer_Alt {
 background-color: #555555 !important;
}

.yafnet .quote.outmost {
 padding-bottom: 0 !important;
 margin-bottom: 0 !important;
}

.postContainer .quote.outmost {
 background-color: #555555 !important;
}

.postContainer .innerquote.outmost {
 background-color: #555555 !important;
}

.postContainer_Alt div.postedLeft {
 border-bottom-color: #383838 !important;
}

/*Signature*/
.yafnet div.yafsignature {
 max-height: none !important;
}

/*END Posts*/

/*Make it bolder*/
b {
 font-family: arial black !important;
 color: white !important;
}

/*This only works in firefox it seems?*/
br {
 line-height: 1 !important;
}

/*Giant quotes are bad*/
.quote > b {
 font-size: 10pt !important;
}

.yafnet .quote.outmost {
 margin-left: 10px !important;
 margin-right: 10px !important;
}

.userbox {
 font-size: 15px !important;
}

.yafnet tr.forumRowTitle, .yafnet td.header1, .yafnet tr.header1 {
 font-size: 16px !important;
}

.yafnet div.postedLeft {
 font-size: 14px !important;
 height: auto !important;
 margin: 0pt 0pt 1% !important;
 width: 100% !important;
}

#timeDateContainer {
 font-size: 12px !important;
}

.yafnet .yafPageLink {
 font-size: 14px !important;
}

.toggle .toggle-content {
 width: 100% !important;
}

.toggle .expand {
 width: 100% !important;
}

.toggle {
 width: 100% !important;
}

.toggle h2.trigger {
 padding: 0 !important;
 font-size: 17px !important;
 width: 100% !important;
 background-size: 100% 1200% !important;
}

.toggle h2 span {
 position: relative !important;
 float: left !important;
}

.yafnet .smallfont {
 font-size: 11px !important;
}

.yafnet .smallfont a {
 font-size: 14px !important;
}

.yafnet {
 font-size: 14px !important;
}

#footerContainer {
 width: 100% !important;
}

#footerNavigationContainer {
 margin-left: 0 !important;
}

.yafnet .yafPageLink {
 padding: 2px 2px 2px 1% !important;
}

.forum-icon-desc {
 width: auto !important;
 clear: both !important;
}

#footerNavigationContainer {
 width: 100% !important;
}

#footerContainer {
 text-align: center !important;
}

.footer-pegi {
 display: none !important;
}

#eveLogoSmallContainer {
 position: relative !important;
 margin-top: -20px !important;
}

#ftabs .footer1 {
 text-align: right !important;
}

#toTop {
 left: 0.3% !important;
 margin-left: 0 !important;
 z-index: 1 !important;
}

/*Search*/

.search-wrapper {
 width: 100% !important;
}

.results ul li {
 width: 100% !important;
}

.results ul li a.title {
 font-size: 14px !important;
}

.adv-search fieldset {
 width: 100% !important;
}

.adv-search .row {
 width: 100% !important;
}

.adv-search .row label {
  width: 4% !important;
}

.search-wrapper .deco-search {
 left: 0.4% !important;
}

.search-wrapper .main-box {
 padding-left: 60px !important;
 width: 95.5% !important;
 margin-bottom: 20px !important;
}

.search-wrapper h4 {
 float: none !important;
 font-size: 14px !important;
}

.search-wrapper .rss-bookmark {
  font-size: 11px !important;
  position: absolute !important;
  top: 5% !important;
  right: 0pt !important;
}

/*Without this very long words in results don't word-wrap when width is 100%*/
.results {
 float: none !important;
}

/*END Search*/

/*Favorites*/

.fav-wrapper {
 float: none !important;
 width: auto !important;
}

.fav-wrapper .ui-tabs .ui-tabs-panel {
 width: 98% !important;
}

.ui-widget table {
 font-size: 11pt !important;
}

/*Open external link popup*/
.ui-dialog .ui-dialog-content {
 font-size: 10pt !important;
}

/*END Favorites*/

/*This is for eve gate, but it needs more work. Also, I think it's old.

a[href*="pegi.info"], a[href*="pegionline.eu"], a[href*="esrb.org"] {
 display: none !important;
}
*/

}
