css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.football365.com") {



#wrapcentre {margin: 0 25px 0 ! important;}	
/* #wrapnav { border-width: 0px ! important;} */

.gen {
	/*margin: 1px 1px;*/
	font-size: 1.1em ! important;
}
th { background-image: none ! important; }
td {padding: 0px ! important;}
 .tablebg {border-width: 0px ! important; background-color:#8E9133 ! important; } 


.tablebg th { color:#088A4B ! important; background-color:#111C06 ! important; }


p.gensmall{display:inline ! important; font-size:12px ! important; }
 #pageheader {margin:0 ! important; visibility:visible ! important;}
#datebar {display:none}

input {
	color: #5C233D;
	font-family: Tahoma, Verdana, Helvetica, sans-serif ! important;
	font-size: 1.1em ! important;
	font-weight: normal;
	padding: 1px;
	/*border: 0px solid #A9B8C2 ! important; */
	background-color: #FAFAFA;
}

textarea {
	background-color: #FF030F;
	color: #333333;
	font-family: Tahoma, Verdana, Helvetica, Arial, sans-serif ! important;
	font-size: 1.1em ! important; 
	line-height: 1.1em ! important;
	font-weight: normal;
	/* border: 0px solid #A9B8C2 ! important; */
	padding: 2px;
}

select {
	color: #333333;
	background-color: #FAFAFA;
	font-family: Tahoma, Verdana, Helvetica, sans-serif ! important;
	font-size: 1.1em ! important;
	font-weight: normal;
	/* border: 0px solid #A9B8C2 ! important; */
	padding: 1px;
}
h1 { font-family: arial, Helvetica, Arial, sans-serif ! important; }
.row1 { /* border-width: 0px ! important; */ background-color: #B3A925 ! important; font-family: arial, Helvetica, Arial, sans-serif ! important; }
.row2 { /* border-width: 0px ! important; */ background-color: #9E8D0D ! important; font-family: arial, Helvetica, Arial, sans-serif ! important; }
.row3 { /* border-width: 0px ! important; */ background-color: #088A4B ! important; color:white; font-family: arial, Helvetica, Arial, sans-serif ! important; }
a.topictitle { font-family: arial, Verdana, "Myriad Web", Syntax, sans-serif ! important;
               font-size: 1.1em ! important;
               font-weight: normal ! important;
               color:#1A1A05;
 }


a.forumlink {
	color: #051202;
	font-weight: bold;
	font-family: arial, verdana, Helvetica, Arial, sans-serif ! important;
	font-size: 1.2em;
}

.pagination {
	padding: 0px ! important;
	color: black;
	font-size: 1.1em ! important;
	font-weight: normal ! important;
}.postbody {

	font-size: 1.1em ! important;
	line-height: 1.1em ! important;
	font-family: arial, Helvetica, Arial, sans-serif ! important;
	}
.postdetails {
        word-wrap: normal ! important;
        }
.quotetitle, .attachtitle {
	margin: 10px 5px 0 5px;
	padding: 2px ! important;
	border-width: 1px 1px 0 1px;
	border-style: solid;
	border-color: #FF030F;
	color: #333333;
	background-color: #A9B8C2;
	font-size: 0.85em ! important;
	font-weight: bold;
}
a:visited {
	text-decoration: none !important;
}
a:hover{
	color: #FF030F!important;
	text-decoration: underline !important;
}
.copyright {
	color: #444;
	font-weight: normal;
	font-family: Tahoma, Verdana, Verdana, Arial, Helvetica, sans-serif ! important;
}

.titles {
        
	font-family: Tahoma, Verdana, Helvetica, Arial, sans-serif ! important;
	font-weight: bold;
	font-size: .85em ! important;
	text-decoration: none;
}
.quotetitle .quotetitle {
	font-size: 1em ! important;
}
.quotecontent, .attachcontent {
	margin: 0 5px 10px 5px;
	padding: 2px ! important;
	border-color: #A9B8C2;
	border-width: 0 1px 1px 1px;
	border-style: solid;
	font-weight: normal;
	font-size: 1em ! important;
	line-height: 1.1em ! important;
	font-family: arial, Verdana, Helvetica, Arial, sans-serif ! important;
	background-color: #FF030F;
	color: #4B5C77;
}
td {
	padding: 0px ! important;
}
td.profile {
	padding: 0px ! important;
}
	/* specifies width of web search results column */
	td.j {
		width: 75% !important;
	}




.topicauthor.a{
font-family:courier;
color:#1F0C0C;
}

.username-coloured{
font-family:arial;
color:#1F0C0C;

}


.gensmall div{
font-size:10px;
}





}
