css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tv.com") {

#header_bar, .page_leader, .mpu_placeholder, .social_bar, .contributors._standard_module,
.listings, .clips, .sponsored_links, .mad_adcontainer, #headerleader, ._watch_button,
.login_required, ._button._edit, div.show_head ._button, div.show_stats ._button,
._status_label, ._rate_it > .flyout, .write_review, ._button.reply, .add, .edit, .thumbs,
.whats_on, .favorites .heart, .favorites .module_title h2 .icon, .follow_links > iframe:first-child,
#container .COLUMN_B, .marketing, ._review_form,
.episode_summary._standard_module.first_in_column + ._standard_module, /* watch episodes online */
#xxwith_vidsxx /* shows */
{
  display: none !important; 
}

#container, ._col_b.gray_bg ._standard_module, .add_item, .POINTER, .POINTER_LEFT, ._no_results.blurb,
div._bento
{
  background-color: #CDCDCD !important;
}

div._bento
{
  padding-bottom: 7px !important;
}

.controls
{
  background-color: #E8E8E8 !important;
}


.POINTER, .POINTER_LEFT, div.photo_navigation a.next, div.photo_navigation a.prev
{
  background-color: #CDCDCD !important;
  background-image: none !important;
  font-weight: bold !important;
}

.story .left_info .links a, .super_title, .tagline, ._nav_tabs li a, .show_stats .text, .scheduled
{
  color: #6D6D6D !important;
}

._standard_module h2, ._standard_module h2 a, ._standard_module .module_title, 
._standard_module .module_title a, .count, li.episode > div.toggle, .story .right_info, 
ul.stats label, .prev_next a, .date, .date a, .byline, .byline a, a.next, a.prev, .ago,
.show_stats .number, .related_shows p, .role, h1#page_title
{
  color: #006699 !important;
}

._nav_tabs li.selected a
{
  background: -moz-linear-gradient(center bottom , #006699 20%, #0099CC 72%) repeat scroll 0 0 #006699 !important;
  color: #FFFFFF !important;
}

.double_feature .blurb .super_title, a.see_more, table.episodes .nums a 
{
  color: #333333 !important;
}

.double_feature .blurb 
{
  background-color: #006699 !important; 
}

#mantle_skin, .container, #tv_header, .tv_footer
{
  background-image: none !important;
  background-color: #000000!important;
}

#tv_header .site_nav li a.on
{
  background-color: rgba(0, 102, 153, 0.8) !important;
}

#tv_header .site_nav li a:hover
{
  background-color: rgba(109, 109, 109, 0.8) !important;
}

.season_episodes._standard_module, .related_shows._standard_module
{
  margin: 0 0 7px 0 !important;
  padding-top: 0 !important;
}

.tv_footer #cbsi_f form input
{
  background-color: #606060 !important;
}
.tv_footer #cbsi_f form input:hover
{
  background-color: #909090 !important;
}

a.tv_logo
{
  left: 0px !important;
  top: 0px !important; 
  height: 60px !important; 
  width: 200px !important;
  background-image: url(data:image/png;base64,) !important;
}

.recent_topics .topic, .ep_title
{
  background: url(data:image/png;base64,) no-repeat scroll 0px 8px transparent !important;

}

.ep_title
{
  color: #FFFFFF !important;
  font-size: 28px !important;
  font-weight: bold !important;
}

}
