css:
/***
Gmail Chat - NtmWay (v1.010) 11:38 13-11-2011
by: Newtomic
http://userstyles.org/styles/46312
***/
@-moz-document domain(mail.google.com)
{
/* chat title > margin */
TABLE.cf.Ht {
	margin: -2px 0 -3px 0 !important;
}
/* chat title img > margin */
IMG.Hi {
	margin: 0 0 -3px -3px !important;
}
/* chat title username > margin */
DIV.Hp {
	margin-left: -3px !important;
}
/* chat buttons > margin */
TABLE.NC {
	margin: -4px 0 !important;
}
/* chat textzone > colors + font + ... */
DIV.nH.ko {
	padding-top: 5px !important;
	line-height: 1.25 !important;
	font-family: "Trebuchet MS" !important;
	color: #FFF !important;
	background-color: #000 !important;
}
/* chat textzone replys > margin */
DIV.tReiP {
	margin: -3px 0 -3px 0 !important;
}
/* chat textzone username > color + margin */
SPAN.kn, DIV.kn {
	color: #4080FF !important;
	margin: 0 0 -8px -4px !important;
}
/* chat textzone system msgs > color */
DIV.kp, DIV.kycZEf {
	color: #555 !important;
}
/* chat textzone alert msgs > color */
DIV.kd {
	color: #0F0 !important;
}
/* chat textzone error msgs > color */
DIV.kg {
	color: #F00 !important;
}
/* chat alert text > colors + margin */
DIV.jw {
	margin: -4px 0 -3px 0 !important;
	color: #FF0 !important;
	background-color: #000 !important;
}
/* chat textbox area > background color */
DIV.jp {
	margin: 0 0 -4px 0 !important;
	background-color: #000 !important;
}
/* chat textbox write text > colors + font + margin + padding */
TEXTAREA.vcZxK {
	margin: -1px 0 -1px 0 !important;
	padding: 0 0 0 4px !important;
	line-height: 1.2 !important;
	font-family: "Trebuchet MS" !important;
	color: #FFF !important;
	background-color: #000 !important;
}
/* chat call top message > padding */
DIV.kR {
	padding: 0 !important;
}
}
