css:
/* AGENT_SHEET */
@-moz-document domain(wikipedia.org),
               domain(wikimedia.org),
               domain(wikinews.org),
               domain(wikibooks.org),
               domain(wikiquote.org),
               domain(wikiversity.org),
               domain(wikisource.org),
               domain(wiktionary.org){
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

:focus { outline: none !important}   /* don't outline focused objects */
* {background-color: transparent !important; border-color: #676d82 !important} 

/* enforce transparent bg */
html, .noprint, .vectorTabs, #simpleSearch, #content, #footer {background: transparent !important}

a{color: #01277e !important}

/* enforce dark fg */
table.plainlinks {background-color: #2a2828 !important}



/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* background */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/





.page-Main_Page * { color: #a5b2b5 !important}

/* solid  */
body{background: #000000 !important}




/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* links colors */

/* article body */
a:link{color: #ffffff !important; font-weight: bold !important}
a:visited{color: #fdc425 !important; font-weight: bold !important}
a:hover{color: #d3b050 !important; text-decoration: none !important; font-weight: bold !important}

/* non existing pages */
a.new:link, a.new:visited{color: #ac7069 !important}
a.new:hover{color: #d26f42 !important}

/* tabs on user preferences page */
#preftoc a{color: #c8c888 !important} 
#preftoc a:hover{color: #fdc425 !important}
#preftoc li.selected a {color: #ffffff !important}
 
/* wikitable */
.wikitable a:link{color: #655c5c !important} 
.wikitable a:visited{color: #fada89 !important} 
.wikitable a:hover{color: #ffffff !important} 

/* plainlinks */
.plainlinks a{color: #aaaaaa !important; font-weight: normal !important}
.plainlinks a:hover{color: #ffe6b3 !important; font-weight: normal !important} 

/* catlinks */
#catlinks a{color: #bbbbbb !important; font-weight: normal !important}
#catlinks a:hover{color: #ffe6b3 !important; font-weight: normal !important} 

/* footer */
#footer a{color: #ffffff !important;  font-weight: normal !important}
#footer a:hover{color: #d3b050 !important; font-weight: normal !important} 

/* ambox/bandeau */
table.ambox a{color: #0714c4 !important} 
table.ambox a:hover{color: #0714c4 !important; text-decoration: underline !important} 
.bandeau a{color: #0714c4 !important} 
.bandeau a:hover{color: #0714c4 !important; text-decoration: underline !important} 
.bandeau-portail-texte a, .cadre_portail a{color: #c8c6df !important} 
.bandeau-portail-texte a:hover, .cadre_portail a:hover{color: #ffffff !important} 

/* thumbnails caption */
.thumbinner a:link{color: #ffffff !important; font-weight: normal !important}  
.thumbinner a:visited{color: #fbae31 !important; font-weight: normal !important} 
.thumbinner a:hover{color: #fbc231 !important; font-weight: normal !important} 

/* specialchars */
#editpage-specialchars a, #mw-editTools a{color: #aaaaaa !important; font-weight: normal !important}
#editpage-specialchars a:hover, #mw-editTools a:hover{color: #ffe6b3 !important; font-weight: normal !important} 

/* message box */
.messagebox a{color: #ffffff !important; font-weight: normal !important}
.messagebox a:hover{color: #f4ca4d !important; font-weight: normal !important}
 
/* info box */
.infobox a{color: #394b62 !important}
.infobox a:hover{color: #ffffff !important}

/* new article - anonymous login */
.permissions-errors a, .mw-newarticletextanon a{color: #a78c88 !important; font-weight: normal !important}
.permissions-errors a:hover, .mw-newarticletextanon a:hover{color: #ffc17f !important; font-weight: normal !important}

/* new article - registered user */
.mw-newarticletext a, #noarticletext a{color: #a2adba !important; font-weight: normal !important} 
.mw-newarticletext a:hover, #noarticletext a:hover{color: #f4edaa !important; font-weight: normal !important}

/* copy warning */
#editpage-copywarn a{color: #f1e37d !important; font-weight: normal !important} 
#editpage-copywarn a:hover{color: #ffffff !important; font-weight: normal !important}

/* alternative links box */
.wikilien_alternatif a:link, .wikilien_alternatif a:visited{color: #ffdb84 !important}
.wikilien_alternatif a:hover{color: #ffffff !important}

/* subtitles */
#siteSub a, #contentSub a{color: #fea201 !important; font-weight: normal !important}
#siteSub a:hover, #contentSub a:hover{color: #fde20b !important; font-weight: normal !important} 

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

/* borders and corners */
h1, .mw-headline, .infobox, .vcard{-moz-border-radius-topleft: 30px !important; -webkit-border-top-left-radius: 30px !important}
h1, .mw-headline, .infobox, .vcard, .catlinks{ -moz-border-radius-bottomleft: 30px !important; -webkit-border-bottom-left-radius: 30px !important}
h1, .mw-headline, .portlet h5{border: none !important}

/* personal toolbar */
#p-namespaces * { margin-left: 10px !important} 
#p-personal li a {color: #9c8494 !important} 
#p-personal li a:hover {color: #fdc425 !important ; text-decoration: none !important}
#p-personal li {padding-right: 15px !important}
#pt-userpage, #pt-anonuserpage, #pt-login, #mw-panel.collapsible-nav div.portal 

/* top navbar */
#p-namespaces li, #p-views li {height: 1.5em !important; margin-top:9px !important}
div.vectorTabs {background-image: none !important}
div.vectorTabs * {background-image: none !important; padding-top: 3px !important ; margin-left: 2px !important}
div.vectorTabs ul li, div#p-cactions ul li {background-image: none !important; background: none !important; border: 1px solid #d7d5c9 !important; -moz-border-radius-topleft: 12px !important; -webkit-border-top-left-radius: 12px !important; -moz-border-radius-topright: 12px !important; -webkit-border-top-right-radius: 12px !important}
div#mv-head { width: 90% !important } 
div.vectorTabs ul li, div#p-cactions ul li {background-color: #97a1cc !important}
div.vectorTabs ul li:hover, div#p-cactions ul li:hover {background-color: #6a7fa2 !important}
div.vectorTabs ul li *, div#p-cactions ul li * { color: #000000 !important}
div.vectorTabs ul li.selected, div#p-cactions ul li.selected {background-color: #bf7a5c !important}
div.vectorTabs ul li.selected:hover, div#p-cactions ul li.selected:hover {background-color: #724223 !important}
div.vectorTabs ul li.selected *, div#p-cactions ul li.selected * {color: #000000 !important}
div#p-cactions ul li.selected {border-bottom: none !important}
div.vectorTabs ul {height: 30px !important}
div.vectorTabs ul li a span:hover, div#p-cactions ul li a:hover {text-decoration: none !important}
/*div.vectorTabs ul li *:hover, div#p-cactions ul li *:hove {color: #ffffff !important} */
div.vectorTabs ul li.selected *:hover, div#p-cactions ul li.selected *:hover  {color: #ffffff !important} 
/* search box */
#searchInput {background-color: #b9b9b9 !important}
#searchButton {display: none !important}
td.os-suggest-result, .suggestions-result {background-color: #f5bb36 !important}
td.os-suggest-result span, .suggestions-result span {color: #000000 !important}
td.os-suggest-result-hl, .suggestions-special, .suggestions-result-current {background-color: #681f1f !important}
td.os-suggest-result-hl span, .suggestions-result-current span {color: #ffffff !important}
.suggestions-special {background-color: #d8c753 !important}
.suggestions-special:hover {background-color: #681f1f !important; color: #ffffff !important}
.special-label {color: #000000 !important}
.special-label:hover {color: #ffffff !important}
#simpleSearch {border: none !important; background:none !important}
#simpleSearch input#searchInput {border: 1px solid #f8b331 !important; -moz-border-radius: 20px !important;  -webkit-border-radius: 20px !important}

/* left side bar */
#mw-panel a {color: #6b7984 !important}
#mw-panel a:hover {color: #fdc425 !important}
#mw-panel h5 {color: #ffffff !important}
#mw-panel h5:hover {color: #ff0000 !important}
#mw-panel.collapsible-nav div.portal h5 { background-position: 0 8px !important}

/* content */
#content, .pBody, #footer {-moz-border-radius-topleft: 1em !important; -moz-border-radius-bottomleft: 1em !important; -webkit-border-top-left-radius: 1em !important; -webkit-border-bottom-left-radius: 1em !important; border-right : none !important; border-width: 5px !important}
#content { border: 1px solid #d7d5c9 !important; border-right: none !important}

.toc, textarea, .catlinks {-moz-border-radius-bottomright: 30px !important; -webkit-border-bottom-right-radius: 30px !important}
.toc{-moz-border-radius-topright: 30px !important; -webkit-border-top-right-radius: 30px !important; border-width: 4px !important}
.infobox, .vcard{border-width: 4px !important}
.thumb, .tleft, .tright {border-width: 0px !important}
.thumbinner {border-width: 1px !important; border-bottom-width: 20px !important; -moz-border-radius-bottomright: 20px !important; -webkit-border-bottom-right-radius: 20px !important}
.jsprefs, table.ambox, table.ambox-content, #mw-js-message, .bandeau{-moz-border-radius-topleft: 20px !important; -webkit-border-top-left-radius: 20px !important}
table.ambox, table.ambox-content, #mw-js-message, .bandeau{-moz-border-radius-bottomleft: 20px !important; -webkit-border-bottom-left-radius: 20px !important; border-collapse: separate !important; border-left-width: 20px !important}
.jsprefs, table.messagebox, #userloginForm form, #contributions-footer-box, #talkpagetext{-moz-border-radius-topright: 20px !important; -webkit-border-top-right-radius: 20px !important}
.jsprefs, table.messagebox, #userloginForm form, #contributions-footer-box{-moz-border-radius-bottomright: 20px !important; -webkit-border-bottom-right-radius: 20px !important}
.jsprefs, table.messagebox, #userloginForm form {border-collapse: separate !important; border-right-width: 20px !important; border-bottom-width: 10px !important}

#editpage-specialchars, .mw-editTools .plainlinks{-moz-border-radius-topright: 15px !important; -webkit-border-top-right-radius: 15px !important; border-collapse: separate !important; border-right-width: 15px !important; border-top-width: 15px !important}

div.permissions-errors * {border-color: transparent !important}
div.permissions-errors, div.mw-newarticletextanon {border: solid 2px !important}
div#mw-recreate-deleted-warn {border: double 2px #af0000 !important}
textarea{border: solid 2px #9e4d30 !important; padding-left: 5px !important; padding-right: 5px !important; width: 98.7% !important}
input{border: solid 1px #9e4d30 !important; padding-left: 5px !important}
input[type=text], input[type=password]{border: solid 1px #9e4d30 !important; padding-left: 10px !important; padding-right: 10px !important}
input[type=password]{border-color: #6c1210 !important}
pre{border-style: dotted !important; padding: 1em !important}
.navbox, .collapsible, .autocollapse, .nowraplinks {border-left-style: dotted !important; border-right-style: dotted !important}
.catlinks{border-width: 2px !important; border-bottom-width: 30px !important}
.cadre_portail, #bandeau-portail{border: none !important}

/* footer */
#footer {margin-top: 10px !important}

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

table, pre{color: #a5b2b5 !important}
.thumb{border: none !important}

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* buttons and input areas */

textarea{background-color: #bababa !important; color: #000000 !important;
  border-style: solid !important; border-color: #3f3d3d #b29b9b #b29b9b #3f3d3d !important} 
input, select{background-color: #bababa !important; color: #000000 !important;
  border-style: solid !important; border-color: #3f3d3d #b29b9b #b29b9b #3f3d3d !important} 
input[type=submit]{background-color: #7b889a !important; color: #000000 !important; 
  border-style: solid !important; border-color: #b29b9b #3f3d3d #3f3d3d #b29b9b !important}
input[type=text], input[type=password], select{background-color: #bababa !important; color: #000000 !important; font-weight: normal !important;
  border-style: solid !important; border-color: #3f3d3d #b29b9b #b29b9b #3f3d3d !important} 

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* bullets - use a diamond-shaped bullet within unordered lists */


.toclevel-1, .toclevel-2, .toclevel-3, .toclevel-4, .toclevel-5, .toclevel-6{list-style-image: none !important}
ul#preftoc li{list-style-image: none !important}

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* coloring classes */
h1{color: #bac5c8 !important}
h2, h3, h4, h5, h6, .plainlinks{color: #96adb1 !important}
table.wikitable, table.wikitable *{background-color: #a1a09f !important; border-color: #53220f !important; color: #000000 !important}
table caption{background-color: #667689 !important; border: none !important}
pre{
  background-color: #222222 !important; 
  border-style: dotted !important; border-color: #ffffff !important; 
  padding: 1em !important}

.cquote td{color: #9d7166 !important}
.thumbinner{background-color: #171d25 !important; color: #8c9bae !important}
.infobox{background-color: #8995a4 !important; color: #000000 !important}
.noprint, .noprint td{color: #999942 !important}
.messagebox{background-color: #312f28 !important; color: #a3a099 !important}
.ambox, .bandeau{background-color: #e89721 !important; color: #000000 !important}
.cadre_portail, #bandeau-portail{background-color: #777777 !important; color: #000000 !important}
.mw-newarticletextanon{background-color: #9e1703 !important}
.mw-newarticletext, .noarticletext{background-color: #424951 !important}
.permissions-errors * {background-color: #a50004 !important; color: #000000 !important}
#cpwarn{background-color: #413436 !important; border-color: #ffffff !important}
.autocomment{color: #c0a98b !important}
.comment{color: #b09191 !important}
.prefsectiontip{color: #886e6e !important}

/* coloring other elements */
#bodyContent, #footer{color: #a5b2b5 !important}
#talkheader th{background-color: #565552 !important; color: #000000 !important}
#contributions-footer-box, #catlinks{background-color: #5f5b51 !important; color: #000000 !important}
#talkpagetext{background-color: #666666 !important; color: #000000 !important}
#editpage-specialchars, .mw-editTools .plainlinks{background-color: #585858 !important; color: #000000 !important}
ul#preftoc li, #p-cactions li{background-color: #93815b !important}
ul#preftoc li.selected, #p-cactions li.selected {background-color: #303030 !important; color: #000000 !important}
#siteSub, #contentSub{color: #ecdbcf !important}
#footer{background-color: #333333 !important; margin-bottom: 0px !important}
.wikilien_alternatif{background-color: #23181a !important}

/* change some syntax highlighing unreadable colors */
.kw1{color: #ff0000 !important}
.kw2{color: #1c9b98 !important}
.kw3{color: #ffff00 !important}
.me1{color: #d3ce8f !important}
.me2{color: #ff00ff !important}
.br0{color: #e69623 !important}

/* restoring black text color */
.infobox *, .ambox *, .bandeau *, .cadre_portail *, #bandeau-portail *, .mw-newarticletextanon *, .mw-newarticletext *, .noarticletext *,
#talkheader th *, table.wikitable *, textarea *, input *, select * {color: #000000 !important}

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* provide a coloured background for some black on transparent images */
/* unfortunately you have to add them to the list below.              */
/* Giving a coloured background while using a black background looks awful */

img[src$="/250px-Singleton_UML_class_diagram.svg.png"]
{background-color: #555555 !important}

/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
/* bullets - use Ubuntu coffee bean within unordered lists */



ul#footer-info li, ul#footer-places li, ul#footer-icons li { list-style-image: none !important ; color: #000000 !important}

.toclevel-1, .toclevel-2, .toclevel-3, .toclevel-4, .toclevel-5, .toclevel-6{list-style-image: none !important}
ul#preftoc li{list-style-image: none !important} 

}
