@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("99manga.com") {

/*Remove Iframe*/
iframe{display:none;}
.zhtable{display:none;}
td[width='8']{display:none;}
#Content{
margin:0 auto !important;
}
.cnname a{
font-size:14px !important;
}
#cDiv{
width:82% !important;
}
table[width='780']{
width:81% !important;
}
table[width='568']{
width:80% !important;
}
a{
cursor:pointer !important;
}
a:hover{
color:#cc99dd !important;

}
.input2_top,.input1_top{

}
td[height='97'],div.replb{display:none;}

img[src='/img/logo5.gif']{height:45px;}

.a,.d,.f,.h,.j, .b a:last-of-type{display:none;}
.b{-moz-transform:rotate(90deg);
-moz-transform-origin: left bottom;
  margin-top: -25px;
width:800px  !important;
text-align:left !important;
line-height:25px;
padding-left:10px;
}
.c{position:fixed;top:0;left:0;height:94% !important;}
.c a.p, .c a.n{width:40% !important;height:100% !important;background:none !important;}
.c a.p:hover{border-left:1px dotted #99cc00;}
.c a.n:hover{border-right:1px dotted #99cc00;}
}
