css:
/* AGENT_SHEET */
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace html url(http://www.w3.org/1999/xhtml);
@-moz-document url("chrome://global/content/viewSource.xul"),
               url("chrome://global/content/viewPartialSource.xul"),
               url-prefix("view-source:"){

/*Chrome*/

#viewSource,
#viewSource-toolbox,
.menubar-text,
#status-bar {
  -moz-appearance:-moz-win-glass !important; 
  background:none !important;
  text-shadow:1px 1px 1px #000 !important; 
  color: #aaa !important;
}


#FindToolbar {
  text-shadow:1px 1px 1px #000 !important; 
  color: #aaa !important;
  font-weight: bold !important;
  border-top: 0 !important;
  margin-top:3px !important;
}

#FindToolbar  toolbarbutton:not([disabled]) {
   color: #aaa !important;
}

.findbar-textbox {
   width:250px!important;  
   -moz-appearance:none !important;
   background:rgba(0,0,0,0.7) !important; 
   border:1px solid #444 !important;
   border-radius: 2px !important; 
   font-weight:bold !important;
   color:#eee !important; 
}

#viewSource-main-menubar menupopup,
#viewSourceContextMenu {
   -moz-appearance: none !important;
   background: #111 !important;
   border: 1px solid #444 !important;
   border-radius: 3px !important;
}


#viewSource-main-menubar menu:not([disabled]),
#viewSource-main-menubar menuitem:not([disabled]),
#viewSourceContextMenu menuitem:not([disabled]) {
   color: #aaa !important;
}

#viewSource-main-menubar menuseparator,
#viewSourceContextMenu menuseparator {
   -moz-appearance: none !important;
   border: solid #444 !important;
   border-width: 1px 0 0 !important;
   margin: 0 3px !important;
}

/*Content*/

/*pre {white-space: pre-line !important; }*/ /*<--uncomment to left justify everything--<<*/

html {
   background: #111 !important;
   color: #ddd !important;
}

.start-tag, .end-tag {color: violet !important}

.attribute-name {color: #ddd !important;}

.attribute-value {color: steelblue !important;}

.comment {color: lightgreen !important;}

a {color:#377 !important;}

::-moz-selection {background-color: #aaa !important; color: #000 !important; }

/*--Scrollbars by makondo--*/

scrollbar * { 
-moz-appearance: none !important;
background: none !important; }

scrollbar { 
-moz-appearance: none !important;
background: #111 !important;
border: none !important; 
}

scrollcorner { 
-moz-appearance: none !important;
background: none !important; 
width: 0 !important;
height: 0 !important; 
}

scrollbar thumb { 
-moz-appearance: none !important; 
min-height: 50px !important;
width: 0 !important;
border: none !important;
background:  transparent url("data:image/gif;base64,") center no-repeat !important; 
opacity: .2 !important; 
}

scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"] {
margin-left: 0px !important; 
margin-right: 1px !important; 
}

scrollbar[orient="vertical"] > scrollbarbutton[type="increment"] { 
margin-right: 1px !important; 
}

scrollbarbutton {
border: none !important;
background: transparent url(data:image/png;base64,) center no-repeat !important;
opacity: .2 !important; 
}

}
