css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
チビファンタジー CHIBIFANTASY choiedit
lastupdate:2011/04/18(月) 13:19
*/
@-moz-document domain("chibifantasy.com") {
td {
font-size:80% !important;
}

td font {
font-size:100% !important;
}

a:link {
color:#cc9900 !important;
}
a:hover {
color:#FF8000 !important;
}
a:visited {
color:#339999 !important;
}

iframe {
height:565px !important;
}

form {
margin-bottom:3px !important;
}
input[type="text"] {
padding: 3px;
}
input[type="submit"] {
padding: 3px !important;
letter-spacing:1px;
cursor:pointer;
}
input[type="submit"]:hover {
color:#FF8000 !important;
}
select[name="banchi"] option,
select[name="itemname"] option {
padding: 2px !important;
cursor:pointer;
border-bottom: 1px dotted #ccc;
}
input[type="radio"] {
width:16px;
height:16px;
margin:2px;
cursor:pointer;
vertical-align:middle;
}
input[type="radio"]:checked {
outline: dotted 1px #FF8000;
}

}

/*////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/top.php") {
form {
margin-bottom:0 !important;
}

table table table td input[type="submit"].c1 {
height:18px !important;
color:#cc9900 !important;
background:#fff !important;
}

table table table td input[type="submit"].c1:hover {
color:#FF8000 !important;
}

}

/*////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/main.php") {
table table font:nth-child(2),
table table:nth-child(2) font {
font-size:120% !important;
}

table:nth-child(5) td{
font-size:130%;
font-weight:bold;
}

}

/*////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/home/hpet.php")
,url("http://chibifantasy.com/home/hpet2.php") {
table:nth-child(3) tr:nth-child(1) td {
padding:2px 1px;
color:#fff;
font-size:70% !important;
text-align:center;
white-space: nowrap;
background-color:#333;
}

table:nth-child(3) td:nth-child(1),
table:nth-child(3) td:nth-child(2),
table:nth-child(3) td:nth-child(4){
white-space: nowrap;
}

table:nth-child(3) td{
border-bottom:1px dotted #333;
background-color:#fff;
opacity: 0.8;
}

}

/*////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/home/hitem.php") {
table:nth-child(2) tr:nth-child(1) td{
padding:2px 1px;
color:#fff;
font-size:70% !important;
text-align:center;
white-space: nowrap;
background-color:#333;
}

table:nth-child(2) td:nth-child(1),
table:nth-child(2) td:nth-child(2),
table:nth-child(2) td:nth-child(4){
white-space: nowrap;
}

table:nth-child(2) td{
border-bottom:1px dotted #333;
background-color:#fff;
opacity: 0.8;
}
}

/*////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/home/hset2.php") {
input[value="登録"] {
padding: 60px 30px !important;
}

input[type="text"] {
margin-bottom: 5px;
}

}

/*////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/home/hset3.php") {
form {
padding: 20px 0 !important;
background:#efefef;
}

input[type="submit"] {
padding: 3px 30px !important;
}

}

/*////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/home/chat/chat.php") {
input[type="text"] {
width:400px;
}

p{
visibility: hidden;
height:0;
}

}


/*////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/start.php") {
body {
background:#fff !important;
}

}

/*////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/battle2.php") {
td {
padding:3px;
}

font table td font {
font-size:110% !important;
}

}

/*アイテム並べ替え////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/home/hitemn.php")
,url("http://chibifantasy.com/home/hpetn.php") {
b {
padding-left:5px;
color:#339999;
}

font {
font-size:90% !important;
}

hr {
border: dotted #ccc;
border-width: 0 0 1px 0;
}

}

/*壁紙変更////////////////////////////////////////*/
@-moz-document url("http://chibifantasy.com/home/zpro.php") {
form select{
height:300px;
display:block;
}
form select option {
padding: 2px !important;
cursor:pointer;
border-bottom: 1px dotted #ccc;
}

}
