@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {
#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,

#dashboard_post_queue, #dashboard_followers, #dashboard_members{

background-image:url('http://i.imgur.com/Zr1Fv.jpg') !important;

background-repeat:repeat;

background-attachment:fixed;
font-size:12px !important;}

h1,h2,h3,h4,h5,h6{}
a, a:visited{}
a:hover{color:#555 !important}
