#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



    border:0px!important;



    background: rgbargba(245,237,214,0.8) !important;



}







.post_question, 



#left_column #posts .post .permalink {



    background: rgba(245,237,214,0.8) !important;



}







.post_question_nipple, .arrow{



    background:transparent!important;}







#search_controls{



border-bottom-color: transparent !important;



