    background: rgba(245,237,214,0.8) !important;



}





#help_docs:hover, 



#all_of_tumblr:hover,



#everyone_i_follow:hover,



#my_posts:hover{



    background: rgba(245,237,214,0.8) !important;



border-bottom:0px #fd5167 solid !important;



border-top:0px #fd5167 solid !important;



}







#nav .nav_item.active,



#nav .nav_item .nav_menu,



