css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.cpcc.edu") {

div#region-content {font-size:1.1em !important; font-family:Verdana !important;}

ul#portal-siteactions li a {font-family:Verdana !important;}
ul#portal-siteactions li a:hover {color:blue !important;} 
div#region-content h1 {font-size:1.3em !important;}
div#region-content h2 {font-size:1.2em !important;}
div#region-content h3 {font-size:1.1em !important;}
div#region-content a {color:blue !important;}
dl#portlet-navigation-tree {}
ul#sections-list {font-size:1.0em !important;}
ul.contentViews li {font-size:1.1em !important;}
ul#portal-globalnav {font-size:0.9em; font-weight:bold !important;}
div#portal-breadcrumbs {font-size:1.0em !important;}
div#portal-breadcrumbs a:hover {color:blue !important;} 
dl.actionMenu {font-size:1.1em !important;}
div.googlesa a {color:blue !important;}
div.googlesa span.gsa_url {color:gray !important;}
div.googlesa dd {margin-bottom:1.5em !important;}
span.gsa_date {font-weight:normal !important;}
div#LSResult a {color:blue !important;}
div#LSResult {font-size:1.3em !important; }
p.documentDescription {font-weight:normal !important; font-size:5px !important; color:gray !important;}
div#portal-footer {font-size:1.1em; font-family:Verdana;}
#portal-breadcrumbs { background: #fff1d1 !important; }
#portal-columns { background: #ffffff !important; }
body { background: #fff1d1 !important; }
#visual-portal-wrapper { max-width: 1024px !important;}
div#content {min-height:500px !important;}

ul#sections-list li a:hover{background-color:#fff1d1 !important;}

ul.selected {border-bottom: none !important;}
ul.selected li {background-color:#ffffff !important;}
div#portal-footer {font-size:.8em !important;}
li.navTreeItem a:hover{background-color:#fff1d1 !important;}
ul#sections-list li {font-family:verdana !important;}
li.navTreeItem {font-family:verdana !important;}
a.navTreeCurrentItem {background-color:#fff1d1 !important;}
ul#sections-list li ul {border-bottom:1px solid #228772 !important;}}
