css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(kongregate.com)
{
.hide_ad_always,
.homepageads,
.adcontainer,
.adspacer,
#feature_header,
#nav_home,
#nav_community,
#nav_developers,
#nav_help,
#subnavwrap,
.game_table>tbody>tr:first-child,
#main_tab_set,
#achievement_pane_title,
.high_scores,
#holodeck_chat_promotion,
.what_are_achievements_icon,
#rightcontainer,
#belowgame_achievements,
#footer
{
	display: none !important;
}
.badges_set1,
#gamepage_header h1,
#leftcontainer, #gameinfo, .game_details, #comments_list, .comment_body
{
	width: auto !important;
}
.badges_set1 .badge_row
{
	float: left !important;
}
h1,
#full-nav-wrap,
#gamepage_header
{
	height: auto !important;
}

/* tabs */
#game_tab_pane,
#chat_tab_pane,
#signup_tab_pane,
#facebook_signup_form_content,
#signup_form_content,
#next_steps_tab_pane,
#accomplishment_awarded_tab_pane,
#accomplishment_group_awarded_tab_pane,
#share_tab_pane,
#high_score_alert_tab_pane,
#shared_content_welcome_tab_pane,
#avatar_tab_pane
{
	display: none !important;
}
#alert_tab_pane
{
	z-index: 100;
}
#achievements_tab_pane
{
	position: absolute !important;
	display: block !important;
}
}
