css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("chrome://speeddial/content/speeddial.xul")
{

.thumbnail-title
{
	font-family: Segoe UI !important;
	font-weight: 400 !important;
	color: rgb(0, 0, 0) !important;
}

#speeddialWindow
{
	background: rgb(0, 0, 0) !important;
}

.thumbnail-title,
.thumbnail
{
	cursor: pointer !important;
}

.thumbnail,
.speeddialThumb,
.speeddial-container
{
	margin: 0 !important;
	padding: 0 !important;
	background: 0 !important;
}

.thumbnail
{
	padding: 0 !important;
	margin: 5px !important;
	border-radius: 2.5px !important;
	opacity: .6 !important;
	border: 3px solid rgb(166, 166, 166) !important;
	box-shadow: none !important;
}

.thumbnail:hover
{
	opacity: 1 !important;
	border-color: rgb(91, 146, 213) !important;
	box-shadow: 0 0 16px rgb(91, 146, 213) !important;
	background: -moz-radial-gradient(top, ellipse farthest-side, rgb(51, 51, 51), rgb(0, 0, 0)) !important;
	-moz-transition: all 200ms ease !important;
}

.thumbnail:hover .thumbnail-title
{
	font-weight: 700 !important;
	-moz-transition: all 100ms ease !important;
}

.thumbnail,
.speeddialThumb
{
	color: rgb(255, 255, 255) !important;
	background: 0 !important;
}

.thumbnail
{
	background: rgba(0, 0, 0, .1) !important;
}

.speeddial-top-middle
{
	border: 0 !important;
	padding: 4px !important;
	background: rgb(166, 166, 166) !important;
}

.thumbnail:hover .speeddial-top-middle
{
	background: rgb(91, 146, 213) !important;
}

.thumbnail > stack > vbox
{
	border: 0 !important;
}

.speeddial-top-left,
.speeddial-top-right,
.speeddial-middle-left,
.speeddial-middle-middle,
.speeddial-middle-right,
.speeddial-bottom-left,
.speeddial-bottom-middle,
.speeddial-bottom-right
{
	display: none;
}

.speeddial-reload-button,
.speeddial-close-button
{
	cursor: default !important;
	list-style-image: none !important;
}

.speeddial-reload-button
{
	background: url(data:image/png;base64,) !important;
}
.speeddial-close-button
{
	background: url(data:image/png;base64,) !important;
}

.speeddial-reload-button:hover
{
	background: url(data:image/png;base64,) !important;
}
.speeddial-close-button:hover
{
	background: url(data:image/png;base64,) !important;
}

}
