css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

/* userstyles */

.message .message-inner .message-content .linked-text,
.tweet-text {color: black !important;}

/* .tweet-full-name, .full-name, ._timestamp,
.message-inner .message-content ._old-timestamp,
.message-inner .message-content */
._timestamp  {color: #AE2268 !important;}

.user-description,
.stream-title h2 {color: navy !important;}
.twitter-atreply {color: green !important;}
.tweet-meta .icons {color: #A40C7E !important;}

.copyright {color: #C00F68 !important;}
.user-rec-inner h3,
.definition .promo,
.trends-inner .trend-location {color: #267026 !important;}

.profile-header .profile-info .bio,
.tweet-row .tweet-timestamp,
.user-full-name-and-label .user-full-name {color: #A40C7E !important;}

.tweet-box .tweet-box-title h2 {color: #585752 !important;}

.user-name-info .user-name-and-actions .user-full-name
 {color: #F2117F !important;}

.tweet-content .tweet-row .content .latest-tweet-timestamp ._old-timestamp,
.tweet-content .tweet-row .content .latest-tweet-timestamp ._timestamp,
.user-meta,
.user-meta .bio .content {color: #AE2268 !important;}

.user-meta .user .pretty-link .at
 {color: black !important;}

.user-meta .user .pretty-link .at-text
 {color: #265E15 !important;}

/* tweets */
.tweet .content {color: black !important;}

.tweet .content .stream-item-header a .username s,
.tweet .content .stream-item-header a .username b,
.tweet .content p.js-tweet-text a.twitter-atreply b,
.tweet .content p.js-tweet-text a.twitter-atreply s
{ color : #008000 !important; }

/* retweeted */
.tweet .content .stream-item-footer .context .with-icn {color: #AE2268 !important;}
.tweet .content .stream-item-footer .context .with-icn .pretty-link b {color: #0084B4 !important;}

.tweet .content .stream-item-footer a.details b .expand-stream-item span,
.tweet .content .stream-item-footer a.details b .collapse-stream-item
{color: #008080 !important;}

.tweet .content .expanded-content .tweet-details-fixer .tweet-stats-container ul.stats li.stat-count a.request-retweeted-popup,
.tweet .content .expanded-content .tweet-details-fixer .tweet-stats-container ul.stats li.stat-count a.request-favorited-popup
{color: #800000 !important;}
/* end - tweets */

/* user account data */
#page-container .dashboard .mini-profile .profile-summary a.account-summary .content .account-group .metadata
{color: #AE2268 !important;}

#page-container .dashboard .mini-profile .js-mini-profile-stats-container .js-mini-profile-stats li a
{color: #800000 !important;}

/* Who to Follow */
.js-account-summary .content .js-user-profile-link .js-action-profile-name .username,
.js-account-summary .content .js-user-profile-link .js-action-profile-name .username s
{ color : #008000 !important; }

.js-account-summary .content .metadata
{color: #AE2268 !important;}

.js-account-summary .content .metadata a.pretty-link,
.js-account-summary .content .metadata a.pretty-link b
{color : #008000 !important; }

#page-container .dashboard .site-footer .flex-module .flex-module-inner ul li a
 {color: #205391 !important;}

}
