css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("facebook.com")

{



/* background image */

body:not(.transparent_widget):not(.connect_widget), .showOnceInteracted {background-image:url(http://fc01.deviantart.net/fs27/f/2008/169/7/a/Doggy_Gir_Wallpaper_by_TresMaxwell.jpg) !important;

background-attachment:fixed !important;

background-size:100% 100% !important;

}



/* alpha background */

#contentCol, .UIIntentionalStory_Message, .UIStory, .profile_color_bar, #pageFooter, .uiSideNav, #left_column, .profile_info_container, .profile-friends, #rightcolumn, .fbxWelcomeBox, .single_photo_header, .fbGroupedNotifications, .navMoreLess, #profile-friends-footer {

 background-color:rgba(255, 255, 255, 0.5)

 !important;

}

.ufiItem, .uiMorePagerPrimary, .uiSideHeader, .uiHeaderNav, .uiHeaderSection {

background-color:rgba(201, 151, 105, 0.7) !important;

}



/* hover */

.uiStreamStory, .photocaption_text, .fbxWelcomeBox, .mbm:not(.uiHeader), .uiGrid, #pagelet_mutual{

-moz-transition-property: background-color;

  -moz-transition-duration: 0.4s;

  -moz-transition-timing-function: ease-out;

-webkit-transition-property: background-color;

  -webkit-transition-duration: 0.4s;

}

.uiStreamStory:hover, .photocaption_text:hover, .fbxWelcomeBox:hover, .mbm:hover:not(.uiHeader), .uiGrid:hover, #pagelet_mutual:hover {background-color:rgb(0,100,0) !important;

-moz-transition-property: background-color;

  -moz-transition-duration: 0.4s;

  -moz-transition-timing-function: ease-out;

-webkit-transition-property: background-color;

  -webkit-transition-duration: 0.4s;

}



/* link colour */

.fbxWelcomeBoxName, .fbxWelcomeBoxProfileLink, .uiAttachmentTitle a, .attachmentName, .fwn, .passiveName, .uiStreamMessage a, .UIImageBlock_Content a, .fbProfileByline a, .fcg a, .fwb:not(.ginormousProfileName), #profile-friends-footer a, .UIActionLinks, .like_link, .comment_link input, #photoinalbum a, .phototag_name, .phototag_name a, #rightcolumn a, .single_photo_header a, .navMoreText, .auxiliary a, .fwb a, .stat_elem, .visible a, .friendBrowserAddAsFriend a, .fbGroupSummary a, .navMoreLess a {

 color:rgb(255,69,0) !important;

}



/* top nav bar */

#headNav, .dialog_title, .fbNubFlyoutTitlebar {

 background:-moz-linear-gradient(left, rgb(0, 100, 0)

 !important;}

#headNav {

 border-top-left-radius:6px !important;

 border-top-right-radius:6px !important;

}

#pageFooter {

border-bottom-left-radius:6px !important;

border-bottom-right-radius:6px !important;

}

#contentCurve {

display:none !important;

}

}
