css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("huffingtonpost.com") { /* Stylish */

.summary-facepile .stats,
.next-page .more-text,
.next-page .count-text,
.reaction_pannel_v3 script,
.facebookvote_v2 script,
blockquote,
div.entry_body_text p
 {color:black !important;}

.color_696969 {color: #202020 !important;}

.snp_read {color: red !important;}
.snp_commented_on a {color: #078BC5 !important;}
.snp_commented_on {color: green !important;}
.snp_friend_stats_activity .descr, .fan-count
 {color: teal !important;}

textarea.comments_textarea { border: thin solid grey !important; }

.comment_body .comment_time {color: #900040 !important;}

.footer-link .link-text,
#not_logged_user a,
#wendybird_user a,
.author_fans a,
.becomefan a {color: #000098 !important;}

.author_name .bio_teaser, .microbio,
.author_name .bio_teaser #comments_microbio_text
 {color: #007000 !important;}

#not_logged_user .masthead_date,
#wendybird_user .masthead_date, #wendybird_user_name,
.comment_item .comment_top .comment_top_inner .author_name .comment_time,
.history a, a.timestamp,
.comment_permalink, .comment_share_link
 {color: #AE2268 !important;}

.user-info .designation
 {color: #00AE2C !important;}

}
