@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {

/* SOME DISPLAY FIXES */

li[data-ft]
{
     padding-left: 1em;
     border-top: 1px solid !important;
}

li[data-ft] .bfb_post_action_container
{
     border: 1px solid !important;
     margin-top: 3px;
}

li[data-ft] .commentList *
{
     background: transparent !important;
}

li[data-ft] .actorPhoto img
{
     border: 1px solid !important;
}

/* COLORS */

li[data-ft*="\"s_obj\":1,"] /* attending */
{
     background: rgba(0,128,0,.1);
}

li[data-ft*="\"s_obj\":8,"] /* like */
{
     background: rgba(0,255,0,.3);
}

li[data-ft*="\"s_obj\":5,"]  /* share link */
{
     background: rgba(0,128,255,.2);
}

li[data-ft*="\"s_obj\":9,"]  /* via app? */
{
     background: rgba(0,255,255,.1);
}

li[data-ft*="\"s_obj\":7,"]  /* photo & tags*/
{
     background: rgba(192,255,0,.2);
}

li[data-ft*="\"s_obj\":11,"] /* text status */
{
     background: rgba(0,32,64,.12);
}

li[data-ft*="\"s_obj\":6,"]  /* photo albums? */
{
     background: rgba(192,255,0,.2);
}

li[data-ft*="\"s_obj\":12,"] /* friends */
{
     background: rgba(255,128,0,.3);
}

}
