css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://b.hatena.ne.jp/entry") {
  #top_keyword,
  #add-bookmark-container,
  /* 新着、人気のブコメがタブ分けされたので人気のブコメの非表示化を解除
     非表示でおｋな方は、↓のコメントアウトを解除して下さい */
  /*
  #scored-bookmarks,
  */
  #top-ad-line,
  #asin-rel-item,
  #asin-sponsor-item, /* 2011.11.30追加 */
  /* お気に入りユーザー非表示でOKな方は、↓のコメントアウトを解除して下さい */
  /* #favorite-bookmarks, */
  div.curvebox-header,
  div.curvebox-bottom,
  div[class*="ads"],
  div.tweets,
  span.retweet-count,
  div.asin-container,
  a.inplace-report-bookmark,
  div.rakuten-widget /* 2012.04.03 追加 */
  {
    display: none !important;
  }
  div.curvebox-body {
    border-radius: 5px !important;
    border: solid 1px #ccc !important;
    margin-bottom: 20px !important;
  }
  #hatena-bookmark-news-header {
    margin-top: 0 !important;
  }
  #user-navigator, .user-navigator-large {
    height: 20px !important;
    width: 33px !important;
    margin-left: 20px !important;
  }
  #user-navigator-subwindow {
    margin-top: 2px !important;
    margin-left: 20px !important;
  }
  ul.bookmark-list li {
    padding: 5px 30px !important;
  }
  ul.bookmark-list li:nth-child(even) {
    background-color: #efefff !important;
  }
  ul.bookmark-list span.comment, ul.bookmark-list li div.header {
    display: inline !important;
  }
  ul.bookmark-list img.profile-image {
    height: 16px !important;
    width: 16px !important;
    padding-left: 20px !important;
    margin-top: 3px !important;
  }
  div.mode-more ul.bookmark-list li img.profile-image {
    padding-left: 15px !important;
  }
  div.mode-more ul.bookmark-list li a.username {
    margin-left: 5px !important;
  }
  #public-bookmarks-header {
    margin-bottom: 0 !important;
  }
  #favorite-bookmarks {
    margin-bottom: 0 !important;
  }
  /* コメントのないブックマークもすべて見る のリンクの表示位置調整 */
  #public-bookmarks {
    position: relative !important;
  }
  #more-link {
    position: absolute !important;
    top: 14px !important;
    right: 5px;
  }
  /* お気に入りユーザーのアイコンを小さくしたい方は、↓をコメントアウトを解除して下さい */
  /*
  #favorite-bookmarks img.profile-image {
    height: 16px !important;
    width: 16px !important;
  }
  */
}
@-moz-document url-prefix("http://b.hatena.ne.jp/entry?mode=more") {
  #user-navigator {
    margin-left: 15px !important;
  }
  #user-navigator-subwindow {
    margin-top: 2px !important;
    margin-left: 15px !important;
  }
  ul.bookmark-list li {
    padding: 5px 30px 5px 16px !important;
  }
}
