css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#TabsToolbar, #PersonalToolbar, #nav-bar, #browser-bottombox, #status-bar, #browser, #sidebar, #sidebar-header, #appcontent, #content > tabbox > tabpanels, #main-window #appcontent {
      background: transparent !important;
}


#titlebar {
       height: 38px !important;      
}

/* :: App Menu Button :: */
#appmenu-button-container {           
      border-radius: 4.5px 4.5px 4.5px 4.5px !important;
      margin-top: 6px !important;
      margin-bottom: -6px !important;
      margin-left: 1px !important;
      padding: 0px !important;                
      background-image: url("chrome://branding/content/icon48.png"), -moz-linear-gradient(rgba(251, 252, 253, 0.95), rgba(246, 247, 248, 0.47) 49%, rgba(231, 232, 233, 0.45) 51%, rgba(225, 226, 229, 0.3)) !important;
      background-repeat: no-repeat !important;   
}

#appmenu-button {
      background: none !important;
      border-top: 0px solid rgba(0, 0, 0, 0.12) !important;
      border-right: 0px solid rgba(0, 0, 0, 0.19) !important;
      border-bottom: 0px solid rgba(0, 0, 0, 0.38) !important;
      border-left: 0px solid rgba(0, 0, 0, 0.12) !important;
      min-width: 50px !important;
      width: 51px !important;
      height: 23px !important;        
      box-shadow: rgba(255, 255, 255, 0.3) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 0px 0px 2px inset, rgba(0, 0, 0, 0.15) 0px 1px 0px 0px !important;
      border-radius: 4.5px !important;
      opacity: 0.8 !important;
      background-clip: padding-box !important;
}

#appmenu-button-container:hover {        
      border-top: 0px solid rgba(0, 0, 0, 0.12) !important;
      border-right: 0px solid rgba(0, 0, 0, 0.19) !important;
      border-bottom: 0px solid rgba(0, 0, 0, 0.38) !important;
      border-left: 0px solid rgba(0, 0, 0, 0.12) !important;
      background-repeat: no-repeat !important;
      -moz-transition-property: box-shadow, opacity, -moz-linear-gradient !important;
      background-image: url("chrome://branding/content/icon48.png"),  -moz-linear-gradient(rgba(251, 252, 253, 0.80), rgba(246, 247, 248, 0.70) 50%, rgba(240, 241, 242, 0.70) 50%, rgba(230, 231, 232, 0.60)) !important;
      opacity: 1 !important;        
      -moz-transition-delay: .3s !important;
      -moz-transition-duration: .5s !important;        
      box-shadow: 0 0 3px 3px rgba(249, 251, 253, .45) !important;
}

#appmenu-button .button-text {
      display: none !important;
}

#appmenu-button dropmarker {
     display: none !important;
}


/* :: Menu Bar ::*/

#main-menubar, #main-menubar:hover {
      color: rgb(0, 0, 0) !important;
      text-shadow: rgb(255, 255, 255) 0px 0px 6px, rgb(255, 255, 255) 0px 0px 6px, rgba(255, 255, 255, 0.4) 0px 1px 0px !important;
      margin-left: 1px !important;
      border-top: 1px solid rgba(0, 0, 0, .12) !important;
      border-right: 1px solid rgba(0, 0, 0, .19) !important;
      border-bottom: 1px solid rgba(0, 0, 0, .38) !important;
      border-left: 1px solid rgba(0, 0, 0, .19) !important;
      background-color: rgba(151, 152, 153, 0.05) !important;
      background-image: -moz-linear-gradient(rgba(251, 252, 253, 0.95), rgba(246, 247, 248, 0.47) 49%, rgba(231, 232, 233, 0.45) 51%, rgba(225, 226, 229, 0.3)) !important;
      border-top-left-radius: 4.5px !important;
      border-top-right-radius: 4.5px !important;
      border-bottom-right-radius: 4.5px !important;
      border-bottom-left-radius: 4.5px !important;
      opacity: .8 !important;     
}


/* :: Personal Toolbar :: */
#PersonalToolbar {
      border-top: 0px none !important;
      border-right: 0px none !important;
      border-bottom: 0px none !important;
      border-left: 0px none !important;
}

/* :: Web Developer Toolbar :: */
#webdeveloper-toolbar {
      background-color: transparent !important;
      border-top: 0px none !important;
      border-right: 0px none !important;
      border-bottom: 0px none !important;
      border-left: 0px none !important;
      text-shadow: rgb(255, 255, 255) 0px 0px 6px, rgb(255, 255, 255) 0px 0px 6px, rgba(255, 255, 255, 0.4) 0px 1px 0px !important;}     
      

/* :: Nav Bar :: */
.toolbarbutton-1 {
      margin-right: 2px !important;
      margin-left: 0px !important;
      height: 24px !important;
      opacity: .8 !important;
      border-top-right-radius: 4.5px !important;
      border-bottom-right-radius: 4.5px !important;
      border-top-left-radius: 4.5px !important;
      border-bottom-left-radius: 4.5px !important;
}

.toolbarbutton-1 dropmarker {
      display: none !important;
}

.toolbarbutton-1:hover {
      -moz-transition-property: opacity !important;
      opacity: 1 !important;
      -moz-transition-delay: .3s !important;
      -moz-transition-duration: .5s !important;
}

textbox#urlbar, searchbar#searchbar textbox {
      background-color: transparent !important;
      background-image: -moz-linear-gradient(rgba(251, 252, 253, 0.95), rgba(246, 247, 248, 0.47) 49%, rgba(231, 232, 233, 0.45) 51%, rgba(225, 226, 229, 0.3));
      height: 24px !important;
      margin-left: 0px !important;
      margin-right: 2px !important;
      border-radius: 4.5px !important;
      border-top: 1px solid rgba(0, 0, 0, 0.12) !important;
      border-right: 1px solid rgba(0, 0, 0, 0.19) !important;
      border-bottom: 1px solid rgba(0, 0, 0, 0.38) !important;
      border-left: 1px solid rgba(0, 0, 0, 0.19) !important;
      box-shadow: rgba(255, 255, 255, 0.3) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 0px 0px 2px inset, rgba(0, 0, 0, 0.15) 0px 1px 0px 0px !important;
      opacity: .8 !important;
}

#urlbar {
      color: rgb(245, 250, 255) !important;
      text-shadow: rgb(0,0,0) 0px 0px 2px, rgb(40, 40, 40) 0px 0px 5px !important;
}

/* :: Tab Bar :: */
.scrollbutton-up, .scrollbutton-down {
      background-color: rgba(151, 152, 153, 0.05)!important;
      background-image: -moz-linear-gradient(rgba(251, 252, 253, 0.95), rgba(246, 247, 248, 0.47) 49%, rgba(231, 232, 233, 0.45) 51%, rgba(225, 226, 229, 0.3)) !important;
      max-height: 24px !important;
      min-height: 10px !important;
      min-width: 12px !important;
      padding-left: 0px !important;
      padding-right: 0px !important;
      margin-top: 6px !important;
      margin-bottom: 6px !important; 
      border-radius: 4.5px !important;
      border-top: 1px solid rgba(0, 0, 0, 0.12) !important;
      border-right: 1px solid rgba(0, 0, 0, 0.10) !important;
      border-bottom: 1px solid rgba(0, 0, 0, 0.38) !important;
      border-left: 1px solid rgba(0, 0, 0, 0.19) !important;   
      background-origin: padding-box !important;
      opacity: 0.8 !important;
      background-clip: padding-box !important;
      -moz-border-image: none !important;
      box-shadow: rgba(255, 255, 255, 0.3) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 0px 0px 2px inset, rgba(0, 0, 0, 0.15) 0px 1px 0px 0px !important;               
}

.scrollbutton-up {
      margin-left: -1px !important;
      margin-right: 2px !important;   
}

.scrollbutton-down {
      margin-left: 2px !important;
      margin-right: 1px !important;   
}

.scrollbutton-up:hover, .scrollbutton-down:hover {
      background: -moz-linear-gradient(rgba(251, 252, 253, 0.80), rgba(246, 247, 248, 0.70) 50%, rgba(240, 241, 242, 0.70) 50%, rgba(230, 231, 232, 0.60)) !important;        
      border-top-color: rgb(109, 109, 109) !important;
      border-right-color: rgb(109, 109, 109) !important;
      border-bottom-color: rgb(109, 109, 109) !important;
      border-left-color: rgb(109, 109, 109) !important;
      display: -moz-box !important;
      -moz-transition-property: opacity, box-shadow !important;
      -moz-transition-timing-function: cubic-bezier(0.0, 0.0, 1.0, 1.0) !important;
      opacity: 1 !important;
      box-shadow: 0 0 2px 2px rgba(249, 251, 253, .45) !important;
      -moz-transition-delay: .3s !important;
      -moz-transition-duration: .5s !important;        
}

#TabsToolbar {
      min-height:30px !important;
}

#TabsToolbar .tabbrowser-tab {
      background-color: rgba(151, 152, 153, 0.05) !important;
      background-image: -moz-linear-gradient(rgba(251, 252, 253, 0.95), rgba(246, 247, 248, 0.47) 49%, rgba(231, 232, 233, 0.45) 51%, rgba(225, 226, 229, 0.3)) !important;
      background-position: 0 0 !important;
      border-top: 1px solid rgba(0, 0, 0, .12) !important;
      border-right: 1px solid rgba(0, 0, 0, .19) !important;
      border-bottom: 1px solid rgba(0, 0, 0, .38) !important;
      border-left: 1px solid rgba(0, 0, 0, .19) !important;
      height: 24px !important;
      margin-top: 2px !important;
      margin-right: 0px !important;
      margin-left: 0px !important;
      margin-bottom: 2px !important;
      border-radius: 7px !important;
      opacity: .8 !important;
      background-clip: padding-box !important;
      background-origin: padding-box !important;
      background-size: auto auto !important;
      -moz-border-top-colors: none !important;
      -moz-border-right-colors: none !important;
      -moz-border-bottom-colors: none !important;
      -moz-border-left-colors: none !important;;
      -moz-border-image: none !important;
      box-shadow: rgba(255, 255, 255, 0.3) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 0px 0px 2px inset, rgba(0, 0, 0, 0.15) 0px 1px 0px 0px !important;        
}

#TabsToolbar .tabbrowser-tab:hover {
      background: -moz-linear-gradient(rgba(251, 252, 253, 0.80), rgba(246, 247, 248, 0.70) 50%, rgba(240, 241, 242, 0.70) 50%, rgba(230, 231, 232, 0.60)) !important;        
      border-top-color: rgb(109, 109, 109) !important;
      border-right-color: rgb(109, 109, 109) !important;
      border-bottom-color: rgb(109, 109, 109) !important;
      border-left-color: rgb(109, 109, 109) !important;
      display: -moz-box !important;
      -moz-transition-property: opacity, box-shadow !important;        
      opacity: 1 !important;
      box-shadow: 0 0 2px 2px rgba(249, 251, 253, .4) !important;
      -moz-transition-delay: .3s !important;
      -moz-transition-duration: .5s !important;        
}

#TabsToolbar .tabbrowser-tab[selected="true"]:not([pinned]) {
      background-image: -moz-linear-gradient(rgb(255, 255, 255), rgba(255, 255, 255, 0.5) 50%), -moz-linear-gradient(rgb(207, 219, 236), rgb(207, 219, 236)) !important;
      margin-top: 2px !important;
      background-position: 0 0 !important;
      border-top: 1px solid rgba(0, 0, 0, .12) !important;
      border-right: 1px solid rgba(0, 0, 0, .19) !important;
      border-left: 1px solid rgba(0, 0, 0, .19) !important;
      border-bottom: none !important;
      border-top-right-radius: 7px !important;
      border-top-left-radius: 7px !important;
      height:26px !important;
      margin-right: 0px !important;
      margin-left: 0px !important;
      margin-top: 0px !important;
      margin-bottom: 0px !important;      
      border-bottom-right-radius: 0px !important;
      border-bottom-left-radius: 0px !important;
      opacity: .88 !important;
      background-clip: padding-box !important;
      background-origin: padding-box !important;
      background-size: auto auto !important;
      -moz-border-top-colors: none !important;
      -moz-border-right-colors: none !important;
      -moz-border-bottom-colors: none !important;
      -moz-border-left-colors: none !important;
      -moz-border-image: none !important;
      box-shadow: rgba(255, 255, 255, 0.3) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 0px 0px 2px inset, rgba(0, 0, 0, 0.15) 0px 1px 0px 0px !important;        
}

#TabsToolbar .tabbrowser-tab[selected="true"]:hover {        
      -moz-transition-property: opacity, box-shadow !important;
      opacity: 1 !important;
      box-shadow: 0 0 3px 3px rgba(249, 251, 253, .5) !important;
      -moz-transition-delay: .3s !important;
      -moz-transition-duration: .5s !important;
}

.tabbrowser-tab {
      color: rgb(245, 250, 255) !important;
      text-shadow: rgb(0, 0, 0) 0px 0px 2px, rgb(40, 40, 40) 0px 0px 5px !important;
}

/* :: Scroll Bar :: */
scrollbar slider thumb, scrollbar slider thumb * {
      background-color: transparent !important;
      width: 10px !important;
      height: 10px !important;        
      background-image: -moz-linear-gradient(rgba(251, 252, 253, 0.95), rgba(246, 247, 248, 0.47) 49%, rgba(231, 232, 233, 0.45) 51%, rgba(225, 226, 229, 0.3));
      border-radius: 4.5px !important;
      border-top: 1px solid rgba(0, 0, 0, 0.12) !important;
      border-right: 1px solid rgba(0, 0, 0, 0.19) !important;
      border-bottom: 1px solid rgba(0, 0, 0, 0.38) !important;
      border-left: 1px solid rgba(0, 0, 0, 0.19) !important;
      -moz-appearance: button !important;
      opacity: .7 !important;
}

scrollbar, scrollbar *{
      -moz-appearance: none !important;
      background:transparent !important;
}

scrollbar scrollbarbutton, scrollbar scrollbarbutton * {
      background-color: transparent !important;
      min-width: 10px !important;
      opacity: .7 !important;
      -moz-appearance: radio !important;
}

scrollbar scrollbarbutton:hover, scrollbar scrollbarbutton:hover * {
      -moz-transition-property: opacity !important;
      opacity: 1 !important;
      -moz-transition-delay: 0.3s !important;
      -moz-transition-duration: 1.0s !important;
}

scrollbar[orient="vertical"] scrollbarbutton[type="decrement"] {
      margin-left: 1px !important; 
}

scrollbar[orient="vertical"] scrollbarbutton[type="increment"] {
      margin-left: 1px !important; 
}

scrollbar[orient="horizontal"] scrollbarbutton[type="decrement"] {
      margin-top: 1px !important; 
}

scrollbar[orient="horizontal"] scrollbarbutton[type="increment"] {
      margin-top: 1px !important; 
}

scrollcorner {
      background-color: transparent !important;
}

/* :: Bookmarks Sidebar Panel, History Panel :: */
#bookmarksPanel, #ybSidebarPanel, #history-panel {
      background: transparent !important;
}

#sidebar, .sidebar-placesTree  {
      -moz-appearance: none !important;
      color: rgb(245, 250, 255) !important;
      text-shadow: rgb(0,0,0) 0px 0px 2px, rgb(40, 40, 40) 0px 0px 5px !important;
      opacity: .85 !important;
}

#sidebar-header, #viewButton {
      color: rgb(245, 250, 255) !important;
      text-shadow: rgb(0, 0, 0) 0px 0px 2px, rgb(40, 40, 40) 0px 0px 5px !important;
      background-color: transparent !important;
      background-image: -moz-linear-gradient(rgba(251, 252, 253, 0.95), rgba(246, 247, 248, 0.47) 49%, rgba(231, 232, 233, 0.45) 51%, rgba(225, 226, 229, 0.3)) !important;
      height: 24px !important;
      margin-left: 0px !important;
      margin-right: 2px !important;
      border-radius: 4.5px !important;
      border-top: 1px solid rgba(0, 0, 0, 0.12) !important;
      border-right: 1px solid rgba(0, 0, 0, 0.19) !important;
      border-bottom: 1px solid rgba(0, 0, 0, 0.38) !important;
      border-left: 1px solid rgba(0, 0, 0, 0.19) !important;
      box-shadow: rgba(255, 255, 255, 0.3) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 0px 0px 2px inset, rgba(0, 0, 0, 0.15) 0px
1px 0px 0px !important;
      -moz-appearance: none !important;
      opacity: .8 !important;
      background-clip: padding-box !important;
      background-origin: padding-box !important;
      background-size: auto auto !important;
      -moz-border-top-colors: none !important;
      -moz-border-right-colors: none !important;
      -moz-border-bottom-colors: none !important;
      -moz-border-left-colors: none !important;;
      -moz-border-image: none !important;
}

#sidebar-search-label {
      color: rgb(245, 250, 255) !important;
      text-shadow: rgb(0,0,0) 0px 0px 2px, rgb(40, 40, 40) 0px 0px 5px !important;
      opacity: .8 !important;
}

#bookmarksPanel #search-box, #history-panel #search-box {
      background-color: transparent !important;
      background-image: -moz-linear-gradient(rgba(251, 252, 253, 0.95), rgba(246, 247, 248, 0.47) 49%, rgba(231, 232, 233, 0.45) 51%, rgba(225, 226, 229, 0.3));
      color: rgb(245, 250, 255) !important;
      text-shadow: rgb(0,0,0) 0px 0px 2px, rgb(40, 40, 40) 0px 0px 5px !important;
      border-radius: 4.5px !important;
      border-top: 1px solid rgba(0, 0, 0, 0.12) !important;
      border-right: 1px solid rgba(0, 0, 0, 0.19) !important;
      border-bottom: 1px solid rgba(0, 0, 0, 0.38) !important;
      border-left: 1px solid rgba(0, 0, 0, 0.19) !important;
      box-shadow: rgba(255, 255, 255, 0.3) 0px 0px 0px 1px inset, rgba(255, 255, 255, 0.1) 0px 0px 0px 2px inset, rgba(0, 0, 0, 0.15) 0px 1px 0px 0px !important;
      opacity: .8 !important;
      -moz-appearance: none !important;
      -moz-border-top-colors: none !important;
      -moz-border-right-colors: none !important;
      -moz-border-bottom-colors: none !important;
      -moz-border-left-colors: none !important;;
      -moz-border-image: none !important;
}
