css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);



/* ---------- Menubar Menu-Buttons - Iconified - [http://userstyles.org/styles/4662] ---------- */

#navigator-toolbox .menubar-text {
  display: none !important;
}

#file-menu, #edit-menu, #view-menu, #go-menu, #bookmarks-menu, #tools-menu, #helpMenu {
  opacity: 0.8 !important;
  margin-left: 6px !important;
  margin-right: 6px !important;
  background-position: 0px 5px !important;
  width: 16px !important;
  background-repeat: no-repeat !important;
}

#file-menu {
  background-image: url("data:image/png;base64,") !important;
}

#edit-menu {
  background-image: url("data:image/png;base64,") !important;
}

#view-menu {
  background-image: url("data:image/png;base64,") !important;
}

#go-menu {
  background-image: url("data:image/png;base64,") !important;
}

#bookmarks-menu {
  background-image: url("data:image/png;base64,") !important;
}

#tools-menu {
  background-image: url("data:image/png;base64,") !important;
}

#menubar-items #helpMenu {
  background-image: url("data:image/png;base64,") !important;
}

#file-menu:hover, #edit-menu:hover, #view-menu:hover, #go-menu:hover, #bookmarks-menu:hover, #tools-menu:hover, #menubar-items #helpMenu:hover {
  opacity: 1 !important;
}



/* ---------- Menubar Buttons Default Appearance - Disabled - [http://userstyles.org/styles/4996] ---------- */

toolbox#navigator-toolbox menu {
  -moz-appearance: none !important;
  border: none !important;
  background-color: transparent !important;
}
