@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {
#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members{background-image:url('http://img848.imageshack.us/img848/1457/bgv3.png') !important;background-repeat:repeat-y !important;background-attachment: fixed;background-color:#841211 !important;background-position:left top !important}#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(254,247,239,0.7) !important;}
#dashboard_edit_post{background-color: #841211 !important}
#right_column .dashboard_nav_item {border-bottom:0px solid #eee !important;background-image:none  !important}#right_column .dashboard_nav_item .dashboard_nav_title,
                    #right_column .dashboard_nav_item .dashboard_nav_title a,
                    #right_column .dashboard_nav_item a,
                    #right_column .dashboard_nav_item ul.dashboard_subpages,
                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {
                        color:#353635 !important;}
#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
                        color:#888 !important ;
                    }
#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
                        background: none !important;
                    }#left_column #posts .notification .avatar {
                        border-color:transparent !important;
                        background-color:#841211 !important;
                    }
#left_column #posts .notification .avatar:hover {opacity:1 !important;}.post{opacity:1 !important; border-bottom:0px !important; color:#555555}.post:hover{opacity:1 !important}.permalink{background-color: #ffffff !important; background-image:none !important}.active{background-color:#cdd568 !important; color:#353635!important;}.nav_item_nipple{display:none !important}.nav_menu{background-color:#841211 !important; border-bottom:0px !important; color:#353635 !important;}#nav .nav_item.active,#nav .nav_item .nav_menu,#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {background-color:#841211 !important;background-image:none !important;border:0px !important;color:#353635 !important;}#nav .nav_item .nav_menu {border-bottom:0px !important}
#nav .nav_item .nav_menu a {color:#e7bcc6 !important;border-top-color:#322115 !important;}#nav .nav_item .nav_menu a:hover {color:#fbfbfb !important;}.dashboard_switch_blog_menu_header{background-color:#17130d !important;color:#777 !important;}.dashboard_switch_blog_menu_item{background-color:#ffffff !important;color:#777 !important;border:0px !important}.dashboard_switch_blog_menu_footer{background-color:#ffffff !important;color:#777 !important;}.dashboard_switch_blog_menu_wrapper{background-color:#ffffff !important;}#new_post_notice_container{color:#fff !important}#new_post_notice_container a{color:#fff !important}#dashboard_switch_blog_menu_current,#dashboard_switch_blog_arrow_and_divider,#dashboard_switch_blog_arrow_current{background-image:none !important}#footer{background: #841211 !important;background-image:none !important}/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://img853.imageshack.us/img853/5716/navv3.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}
.new_post_label{
font-size:14px !important;
padding-top:5px !important;
font-family:Georgia, Times New Roman, sans-serif !important;
font-style:italic !important;
text-transform: none !important;
color: transparent!important;
letter-spacing:1px !important}
#left_column #posts .notification {
                        background-color:#fef7ef !important;
                        color:#FFCCCC !important;
                        border-bottom-color: #FFCCCC !important
                    }
#left_column #posts .notification .block {
                            color:#FFCCCC !important;
                        }
#left_column #posts .notification.alt {
                        background-color: #fef7ef !important;
border-bottom-color: #ffcccc !important
                    }
#left_column #posts .notification.last_notification,
                    #left_column #posts .notification.single_notification {
                        border-bottom-color:#FFCCCC !important;
                    }

#left_column #posts .notification a {
                        color:#68574a !important;
                        border-bottom:0px !important;
                    }
#left_column #posts .notification blockquote {
                        border-left-color:#eee !important;
                    }
#left_column #posts .notification .avatar {
                        border-color:transparent !important;
                        background-color:#fbfbfb !important;
                    }
#left_column #posts .notification .avatar:hover{
                    }
.notification_type_icon{display:none !important}
h1,h2,h3,h4,h5,h6{color:#F8EBE6 !important}
