css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

body {

background: url(http://i56.tinypic.com/ezo3te.jpg)bottom right #000 no-repeat fixed !important;
}

ol#posts li blockquote,



body.mceContentBodyblockquote {


margin-left: 0 !important;

margin-right: 0 !important;


padding-left: 10px !important;

border-width: 4px !important;

border-color: #570A0A !important;

}

ol#posts li blockquoteblockquote,

body.mceContentBodyblockquoteblockquote {

border-color: #872E2E !important;

}

ol#posts li blockquoteblockquoteblockquote,

body.mceContentBodyblockquoteblockquoteblockquote {

border-color: #AD4F4F !important;

}



ol#posts li blockquoteblockquoteblockquoteblockquote,

body.mceContentBodyblockquoteblockquoteblockquoteblockquote {

border-color: #DC8A8A !important;

}

ol#posts li blockquoteblockquoteblockquoteblockquoteblockquote,

body.mceContentBodyblockquoteblockquoteblockquoteblockquoteblockquote {

border-color: #F1BBBB !important;

}

ol#postsli.notification.single_notification {

border-bottom: 1px solid #f8f8f0;

}









#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {







background: rgba(52,51,51,0.5) !important;







}















#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {







background: rgba(52,51,52,0.5) !important;







    -webkit-border-radius: 20px;







    -moz-border-radius: 20px;







border-radius: 20px;







margin: auto;







padding: 40px auto;







  }















h1 {







color: #000000;







text-shadow: 0 0 0px #f8f8f0;







}















.no_posts_found, h1.dashboard_header {







color: #666666;







}















img#content_top, img#content_bottom {







display: none;







}















#right_columna#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {







background: none;







  }















#right_column .dashboard_nav_item {







background: none;







background-attachment: initial;







background-position-x: 50%;







background-position-y: 100%;







background-origin: initial;







background-clip: initial;







background-color: initial;







}















#right_column .dashboard_nav_item #hide_radar {







background-image: none;







background-repeat-x: no-repeat;







background-repeat-y: repeat;







background-attachment: initial;







background-position-x: 0%;







background-position-y: 0%;







background-origin: initial;







background-clip: initial;







background-color: solid;







}















#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {







background: rgba(0,0,0,0.5);







}















#nav .nav_item.active {







background: rgba(184,184,184,0.5) !important;







  }















#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {







background: rgba(52,51,51,0.5) !important;







  }















ol#postsli.notification.alt {







background-color: rgba(0,0,0,0.5);







}















ol#postsli.notification.first_notification {







background-color: rgba(0,0,0,0.5);







}















ol#postsli.notification {







background-color: rgba(0,0,0,0.5);







border-bottom: 1px solid #f8f8f0;







}















ol#postsli.notification.last_notification {







border-bottom: 1px solid #f8f8f0;







}















ol#postsli.post .permalink {







background: rgba(52,51,51,0.5);







}















#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {







background: rgba(0,0,0,0.5);







border: solid #f8f8f0;







}















ol#postsli.post .post_avatar{







border-bottom: 0px solid #f8f8f0;







}























#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {







border-top: 1px solid #f8f8f0;







border-bottom: 1px solid #f8f8f0;







}















#right_columna#dashboard_switch_blog_menu_current:hover {







background: none;







}















#right_columna#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {







background: none;







}




ol#postsli.post.new_post {



background: url(http://i55.tinypic.com/n5r7v5.jpg) center no-repeat;



}

img[src*="http://i55.tinypic.com/n5r7v5.jpg"]{





width:0;



height:70px;



padding-right:250px;



opacity: 0;



}



.new_post_label{



font-size:14px !important;



padding-top:7px !important;



font-family: Arial Unicode MS !important;



font-style: !important;



text-transform: lowercase !important;



color:#222 !important;



letter-spacing:1px !important



}












form.dashboard_options_form {







background-color: rgba(0,0,0,0.7);







border-bottom: 1px solid #f8f8f0;







border-bottom-left-radius: 10px 10px;







border-bottom-left-radius: 10px 10px;







border-bottom-right-radius: 10px 10px;







border-bottom-right-radius: 10px 10px;







border-top-left-radius: 10px 10px;







border-top-left-radius: 10px 10px;







border-top-right-radius: 10px 10px;







border-top-right-radius: 10px 10px;







color: #cccccc;







display: block;







font-size: 13px;







margin-bottom: 25px;







padding: 15px 22px;







}















form.dashboard_options_form .option_container {







border-top: 1px solid #f8f8f0;







}















.no_posts_found, h1.dashboard_header {







color: #333333;







}















.answer_container {







background-color: #000000;







border-bottom: 0px solid #f8f8f0;







}















#nav .nav_item .nav_menu {







background-color: #000000;







border-bottom: 2px solid #f8f8f0;







}















#nav .nav_item .nav_menu a {







border-top: 1px solid #f8f8f0;







}















#auto_pagination_loader {







background: none;







border-top: 0px solid #f8f8f0;







}







}















