css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rememberthemilk.com") {

.xtd_task_name {
	display: block ;
	margin-bottom: 3px ;
font-size: 10pt;
}

#detailsbox {
	float:left;
}

#detailscontent {padding: 14px;}
#listbox {
	float: right ;
}

#detailswrap > div {padding-top: 0.1em; }


#appheader {
	position: relative;
	top: 12px;
width: 100%;
}

#listtabs >UL {
	position: fixed;
	width: 100%;
	top: 0px;
	left: 0px;
	right: 0px;
	margin: 0 auto;
}

#listtabs >UL >LI {
padding: 0px;
background: none;
border-bottom: none;
border-top: 1px  solid grey;
}

#listtabs >UL >LI >A {
text-transform: uppercase;
font: 12px/15px "Calibri";
padding-left: 10px;
padding-right: 10px;
margin: 0 2px;
border-size: 2px;
-webkit-border-radius: 0px 0px 2px 2px ;
-webkit-box-shadow: 0px 0px 3px black;
height: 15px;
margin-bottom: 14px;
overflow: hidden;
background: grey;
font-weight: bold;
}

#listtabs >UL >LI.xtab_smartlist > A {background: #565656;}


#listtabs >UL >LI >A:hover,#listtabs >UL >LI.xtab_selected > a {
height: 20px;font-weight:bold;background:#EEE ;padding-top:6px;margin-bottom: 0px;
}

#settingstabs >UL {
	position: fixed;
	width: 100%;
	top: 0px;
	left: 0px;
	right: 0px;
	margin: 0 auto;
}

