css:
@-moz-document domain('ptchan.net'), url-prefix('http://ptchan.net') {
	
html, body {
background:#000 !important ;
color:#FFF !important ;
}
* {
font-family: "Trebuchet MS", Tahoma, Verdana, Arial, sans-serif !important ;
font-size: 10pt !important ;
}
input, textarea {
background-color: #101010 !important ;
color: #ffffff !important ;
border: 1px solid #202020 !important ;
}
a {
background:inherit !important ;
color: gray !important ;
text-decoration: none !important ;
cursor: pointer !important ;
}
a:visited {
background:inherit !important ;
color: gray !important ;
}
a:hover {
color: #FFF !important ;
background:inherit !important ;
text-decoration:underline !important ;
}
a.quotelink {
background:inherit !important ;
color:#ffffff !important ;
}
.logo {
clear:both !important ;
text-align:center !important ;
background:inherit !important ;
font-size:24pt !important ;
color: #ffffff !important ;
width:100% !important ;
font-weight: bold !important ;
}
.postarea {
background:inherit !important ;
}
.postblock {
background: #202020 !important ;
border: 1px solid #303030 !important ;
color: #ffffff !important ;
font-weight: bold !important ;
padding: 2px 5px 2px 5px !important ;
}
.footer {
text-align:center !important ;
font-size:12px !important ;
}
.unkfunc {
background:inherit !important ;
color: #789922 !important ;
}
.filesize {
text-decoration:none !important ;
}
.filetitle {
background:inherit !important ;
color:#606060 !important ;
font-weight:800 !important ;
}
.postername {
background:inherit !important ;
font-size:11pt !important ;
color:#505050 !important ;
font-weight:bold !important ;
}
.postertrip {
background:inherit !important ;
color:#808080 !important ;
}
.oldpost {
background:inherit !important ;
color:#404040 !important ;
font-weight:800 !important ;
}
.omittedposts {
background:inherit !important ;
color:#707070 !important ;
}
.reply {
background:#202020 !important ;
color:#FFFFFF !important ;
border:1px solid #101010 !important ;
border-radius: 5px
}
.replyhl {
background:#000000 !important ;
color:#707070 !important ;
}
.doubledash {
vertical-align:top !important ;
clear:both !important ;
float:left !important ;
}
.replytitle {
background:inherit !important ;
font-size:18px !important ;
color:#000000 !important ;
font-weight:800 !important ;
}
.commentpostername {
background:inherit !important ;
font-size:11pt !important ;
color:#505050 !important ;
font-weight: bold !important ;
}
a.quotejs {
color:#909090 !important ;
text-decoration: none !important ;
}
a.quotejs:hover {
font-weight:bold !important ;
}
.adminbar {
text-align: left !important ;
}
#watchedthreads {
background-color: #000000 !important !important ;
border: 1px solid #303030 !important !important ;
	font-size:10pt !important ;
	padding:0px !important ;
	line-height:20px !important ;
}

#watchedthreadsdraghandle{
        color: #000000 !important ;
	border: 0px !important ;
	padding:0px !important ;
	line-height:20px !important ;
}
.thumb { cursor: pointer !important ; border-radius: 5px}
 .reflinkpreview{ background-color: #000000 }}
