css:
/*******************************/
/* Dreams FC                   */
/* Date: 16/04/2011            */
/* Made by: rei85              */
/*******************************/

@-moz-document domain("hattrick.org") {


#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
        background: #000000 !important;
        /*background: url(page/top.png) bottom left no-repeat !important;*/
}

#hattrick,#hattrickNoSupporter 
{
        margin:auto!important; 
        position:relative;
        left:-5px;width:994px!important;
}

#footer a
{
        color: white !important; 
}
#ticker
{
	position: absolute !important;
	top: 30px !important;
	left: 380px !important;
}


#ticker div a
{
	padding-bottom: 2px !important;
	color: #ffffff !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}

#shortcuts
{
	margin-top: 0px !important;
	margin-left:0px !important;
}

#teamLinks
{
        color: #ffffff !important;
        margin-top: 32px !important;
	margin-left: 7px !important;
}

div#teamLinks a
{
        color: #ffffff !important;
}

/*Users online - week - date - time*/

div[id="online"] 
{
        color: white !important; 
        /*float:right!important;*/
        margin-right:0pt!important;
        margin-top:23pt!important;
}

div[id="time"]
{
        color: white !important;
        /*float:right!important;*/
        margin-right:0pt!important;
        margin-top:23pt!important;
}

/*Page background*/
body
{  
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/bgcorto-1.gif) !important;
}

/*centre title*/

h1 
{
        color: #808080 !important;
        border-bottom: solid thin darkgrey !important;
        background: #eeeeee !important;
}

/*all boxHead*/
h2 
{
        /*color: white !important;
        border-bottom: solid thin #eeeeee !important;
        background: #0042a5 !important;*/
}

/*centre news*/
.boxBody h2 
{ 
        color: #0042a5 !important;
        border-bottom: solid 1px #c70000 !important;
}



/*-----------boxHeads-----------*/

/*Left menu*/
.subMenuBox .boxHead
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/menu.gif) !important;
}

.subMenuBox .boxHead .boxLeft
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/menu.gif) !important;
}

/*Centre box*/
.main .boxHead h2 
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/menu.gif) !important;
}

/*Right menu*/
.sidebarBox h2 
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/menu.gif) !important;
}

/* Press, GB, Team events */
.main .mainBox h2 
{
	border-bottom: solid 1px #c70000 !important;
	background-color: #fff !important;
	color: #0042a5 !important;
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}


/*Central header paths*/

h2 a 
{
        color: #ffffff !important;
}

h2 a:hover 
{
        color:#c70000 !important;
}

/*News and team events' date*/
.date
{
	color: #af2f3c !important;
	padding-right: 8px;
	font-weight: 400;
        font-weight: bold !important;
}

/*Left Menu titles*/
h3 
{
        color: #0042a5 !important;
        border-bottom: solid thin #c70000 !important;
        background: #eeeeee !important;
}

/*General Links*/
a
{
        color: #03789a !important;
	text-decoration: none !important;
}

/*----subMenu and sidebar links----*/
.subMenuBox a
{
	text-decoration: none !important;
	color: #003071 !important;
}

.sidebarBox a 
{
	text-decoration: none !important;
	color: #003071 !important;
}

/*Bot teams link*/

div a[class="shy"] 
{
        color: gray !important;
}

/*Hide Hattrick logo*/
#logo 
{
        background: none !important;
}

#stageLogo {
        background: none !important;
}

/*Hide ticker's stars*/
#ticker div 
{
        /*background: none !important;*/
}



/* central page background */
#content 
{
        margin-top: 0px !important;
        background-image: none !important;
        background-color: #829bc0 !important;
}

/*Image header (banner)*/
#header 
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/dreams_banner3.jpg) !important;
	height: 120px !important;
	padding-top: 40px !important;
}

/*--------Top menu settings--------*/
#menu {

	height: 30px !important;
	margin-top: 5px !important;     
        bottom: -0px !important;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/menu.gif) !important;
}

#menu a {

	line-height: 30px !important;
        color: #ffffff !important;
        /*-moz-border-radius: 2% !important;*/        
        /*background: #808080 !important;*/
}

div#menu a:hover 
{
        color:#FFFFFF !important;
        background:#af2f3c !important;
}

a:hover
{
	color: #af2f3c !important;
}

/*bottom white line*/
div[id="bottom"]
{
        background: #829bc0 !important;
}

/*Forum*/
.cfHeader
{
        background: #0042a5!important;
}

#cfPollHeader
{
	background: #0042a5!important;
	padding: 5px 10px;
	font-weight: bold;
	color: #ffffff;
}

.cfWrapper
{
	border: solid 1px #0042a5 !important;
	margin: 0 0 12px 0;
}

.cfWrapper div.signature a, div.signature-trunc a, div.signature-expand a {
    color: #999999 !important;
    text-decoration: underline !important;
}

.cfHeader a
{
	color: #ffffff !important;
	font-weight: bold;
	text-decoration: none;
}


.cfUserInfo a
{
        color: #003071 !important;
}

.cfMessage a
{
	color: #03789a !important;
}

.folderHeader a:link 
{
	color: #003071 !important;
	font-weight: bold !important;
}

.cfFooter a
{ 
        color: #003071 !important;
}

.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/cross_small.png) no-repeat !important;
}

.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tick_small.png) !important;
}

.folderItem div.unignore:hover
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tickGreen_small.png) !important;
}

div[class="insertHeader"]
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/menu.gif) !important;
}

div[class="insertFooter"]
{
        background: #ffffff !important;
}


/*shortcuts*/

img.scNewMail 
{
	background: transparent url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/Newmail.png) no-repeat !important;
}

/* Tabs Match orders*/
.ui-tabs .ui-tabs-nav li a { float: left; text-decoration: none; padding: 4px 10px 3px 10px; color:#eee; text-decoration:none;  color: white !important;}

/* Tabs Match line up */
.LinkTabActive, .LinkTabNotActive {
    background-color: #2E741B;
    color: #FFFFFF !important;
    float: left;
    font-size: 1em;
    font-weight: 700;
    padding: 0 4px;
    text-decoration: none;
}


/*Foxtrick View by ID*/


#ft_rapidid, #ft_rapidid a
{
    margin-top: 30px !important;
    margin-right: 0px !important;
    color: #ffffff !important;
}

/*Foxtrick TeamPopupLinks*/

ul.ft-popup-list 
{
    color: #000000 !important;
    background-color: #fff !important;
    border: solid 1px #000000 !important;
}

ul.ft-popup-list a
{
    /*color: #000000 !important;*/
}


ul.ft-popup-list > li > a, ul.ft-popup-list > li > span {
    color: #000000 !important;
    cursor: pointer;
    display: block !important;
    font-weight: normal !important;
    padding: 0 !important;
    text-align: left;
    text-decoration: underline !important;
}
ul.ft-popup-list > li:hover {
    background-color: #C3E7C7 !important;
}

div[class*="ad_"]
{
        display:none !important;
}

}
