css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bugs.playzo.biz") {
body { font-size: 75% !important; border: 15px solid #000; background: #000 !important; }
#globalWrapper { font-size: 127% !important; background:  url('http://www.romadoria.de/branding/romadoria/gfx/global/playzo-16.png') no-repeat top center !important; }
#column-one, #content { width: 80em; margin: 70px auto 2em auto !important; float: none; }
#content { background: rgba(255, 255, 255, .95) !important; -moz-border-radius: 1em; -webkit-border-radius: 1em; -o-border-radius: 1em; -moz-box-shadow: 2px 2px 6px rgba(232, 84, 24, .9); -webkit-box-shadow: 2px 2px 6px rgba(232, 84, 24, .9); -o-box-shadow: 2px 2px 6px rgba(232, 84, 24, .9); padding-top: 2em !important; }
#column-one { margin: 0 !important; }

#p-personal { opacity: 0; padding: .3em 0 !important; }
#p-personal:hover { opacity: 1; }
#p-personal * { color: #fff !important; background: none !important; }
#p-personal a:hover { color: #E85418 !important; background: none !important; }
#p-logo { display: none !important; }

h1, h2, h3 { color: #E85418 !important; font-weight: normal !important; }
h1 { font-weight: bold; }
.layouttable { background: none !important; border: 0 !important; }
#toc td,
.box { border-color: #E85418 !important; -moz-box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); -o-box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); -webkit-box-shadow: 2px 2px 3px rgba(232, 84, 24, .4);  box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); padding: 0 !important; -moz-border-radius: .5em; -webkit-border-radius: .5em; -o-border-radius: .5em; border-radius: .5em; }
.box h2 { font-weight: bold !important; background: #E85418 !important; color: #fff !important; padding: .4em .5em !important; -moz-border-radius-topleft: .3em; -moz-border-radius-topright: .3em; -webkit-border-top-left-radius: .3em; -webkit-border-top-right-radius: .3em; -o-border-radius-topleft: .3em; -o-border-radius-topright: .3em;  }
.box h3 { padding: .2em .5em !important; }
.box ul { margin: 1em 1em 1em 2em !important; }

#toc { margin-left: 1em !important; float: right; border: 0 !important; padding: 0 !important; }
#toc td { border: 1px solid #E85418 !important; -moz-box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); -webkit-box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); -o-box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); padding: 0 !important; -moz-border-radius: .5em; -webkit-border-radius: .5em; -o-border-radius: .5em; border-radius: .5em; }
#toc #toctitle { padding: .3em .5em !important; background: #E85418 !important; color: #fff !important;  }
#toc #toctitle h2 { color: #fff !important; }
#toc ul { margin: .3em 1em !important; }

h2 { clear: left; }

#p-cactions { position: absolute !important; top: 85px !important; left: 50% !important; margin: 0 0 0 -45em !important; z-index: 100 !important; width: 68em !important; padding: 0 .5em !important; }
#p-cactions ul { padding: .3em 0 !important; text-align: right !important; }
#p-cactions li { background: none !important; border: 0 !important; }
#p-cactions a { padding: .3em .5em !important; color: white !important; background: none !important; border: 0 !important; }

#p-search { position: absolute !important; top: 85px !important; background: none !important; left: 50% !important; background: rgba(0, 0, 0, .6) !important; margin: 0 0 0 -45em !important; z-index: 99 !important; width: 86.2em !important; height: 2.5em !important; }
#p-search h5 { display: none !important; }
#p-search #searchBody { padding: 0 !important; background: none !important; border: 0 !important; }
#p-search form { float: right; }
#p-search form #searchGoButton { display: none; }
#p-search form div { position: relative; z-index: 101 !important; padding: .3em 1em .3em 0 !important; }
#p-search form #searchGoButton { display: none; }
#p-search form #mw-searchButton { border: 0 !important; background: none !important; opacity: 0; position: absolute; display: block; width: 3.5em; z-index: 102; right: 0; top: 0; height: 2.3em; cursor: pointer; }
#p-search form #searchInput { position: relative !important; top: 4px; z-index: 101; top: 4px; display: block; border: 1px solid #E85418 !important; background: url(http://game.gronenland.com/support/img/icons/magnifier.png) no-repeat top right; width: 15em !important; padding-right: 2.3em !important; color: #eeeeee; }

#p-navigation, 
#p-tb { position: absolute !important; top: 85px !important; background: none !important; left: 50% !important; margin-left: -29em !important; z-index: 103; }
#p-navigation { margin-left: -40em !important; }
#p-navigation h5, 
#p-tb h5 { cursor: pointer; display: block !important; font-size: 100% !important; color: #fff !important; padding: .7em .5em .3em .5em !important; }
#p-navigation .pBody, 
#p-tb .pBody { margin-top: .69em !important; display: block !important; background: rgba(0, 0, 0, .6) !important; border: 0 !important; padding: 0 !important; }
#p-navigation .pBody ul,
#p-tb .pBody ul { list-style: none !important; margin: 0 !important; padding: 0 !important; }
#p-navigation .pBody ul li,
#p-tb .pBody ul li { margin: 0 !important; padding: 0 !important; }
#p-navigation .pBody ul li:last-child,
#p-tb .pBody ul li:last-child { -moz-border-radius-bottomleft: .5em; -moz-border-radius-bottomright: .5em; -webkit-border-bottom-left-radius: .5em; -webkit-border-bottom-right-radius: .5em; -o-border-radius-bottomleft: .5em; -o-border-radius-bottomright: .5em; }
#p-navigation .pBody li a,
#p-tb .pBody li a { color: #fff !important; display: block; padding: .3em .5em !important; }
#p-navigation .pBody a:hover,
#p-tb .pBody li a:hover { background: #fff !important; color: #E85418 !important; text-decoration: none !important; }
#p-navigation .pBody,
#p-tb .pBody { display: none !important; opacity: 1; }
#p-navigation:hover .pBody,
#p-tb:hover .pBody { display: block !important; opacity: 1; }

#footer { display: none; }

#userloginForm form { padding: 0 !important; margin: 1em auto !important; float: none !important; width: 30em !important; border: 1px solid #E85418 !important; -moz-border-radius: 1em; -webkit-border-radius: 1em; -o-border-radius: 1em; border-radius: 1em; }
#userloginForm form h2 { background: #E85418 !important; color: #fff !important; padding: .3em .5em !important; font-size: 120% !important; border: 0 !important; -moz-border-radius-topleft: .8em; -moz-border-radius-topright: .8em;  -webkit-border-top-left-radius: .8em; -webkit-border-top-right-radius: .8em;  -o-border-radius-topleft: .8em; -o-border-radius-topright: .8em; text-align: center !important; }
#userloginForm form > * { padding: 0 .5em !important; }
#userloginForm form table { margin: 0 .5em .5em .5em !important; }

.wikitable { margin: 0 auto 1em auto !important; }
.wikitable tr td:hover { color: #E85418 !important; }
#mw-pages table { background: none !important; border: 0 !important; }

.thumb { border-color: rgba(255, 255, 255, 0) !important; }
.thumbinner { -moz-box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); -webkit-box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); -o-box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); box-shadow: 2px 2px 3px rgba(232, 84, 24, .4); border: 1px solid #E85418 !important; -moz-border-radius: .5em; -webkit-border-radius: .5em; -o-border-radius: .5em; border-radius: .5em; }
.thumbinner a { border: 1px solid rgba(0, 0, 0, 0) !important; -moz-border-radius: .3em; -webkit-border-radius: .3em; -o-border-radius: .3em; border-radius: .3em; }

table.bugzilla { border: 1px solid black !important; border-collapse: collapse !important; margin: 0 !important; width: 100% !important; }
table.bugzilla th { background-color: #CCC; padding: .5em; font-weight: bold; border: 1px solid black !important; }
table.bugzilla td { padding: .5em; border: 1px solid black !important; }
table.bugzilla tr.P1 td { color: #ff0000 !important; }
table.bugzilla tr.P2 td { color: #ff3300 !important; }
table.bugzilla tr.P3 td { color: #000 !important; }
table.bugzilla tr.P4 td { color: #444 !important; }
table.bugzilla tr.P5 td { color: #888 !important; }
table.bugzilla tr.NEW td { font-style: italic !important; }
table.bugzilla tr:nth-child(odd) { background-color: #EEE !important; }
table.bugzilla tr.bz_row_odd { background: none !important; }
table.bugzilla tr.bz_group td { background: #333 !important; color: #E85418 !important; font-weight: bold; font-variant: small-caps !important; }
table.bugzilla, .wikitable { margin: 0 auto 1em auto !important; }
table.bugzilla tr td:hover, .wikitable tr td:hover { color: #E85418 !important; }
}
