css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("themixingbowl.org"), domain("tmb.dj") {
body {
background:none repeat scroll 0 0 #e4aded !important;
color:#444444 !important;
font-family:Verdana,Arial,Helvetica,sans-serif;
font-size:0.7em;
}
#logo  {
background-image:url("http://dl.dropbox.com/u/30897/TMBlogo-red1.png") !important;
}
/*nav bar, top torrents, radio, user stats*/
	#header .navsite .shadow3 {
	background-color:#a64fc2 !important ;
	}
	#header .navuser .shadow3 {
	background-color:#a64fc2 !important;
	}
	#header .nav li ul li { /*menu items*/
	background-color:#ece9d8 !important;
	}
	#user_tt  {
	border:1px solid #000000 !important;
	background-color:#ece9d8 !important;
	}
	#user_tt div {
	border-color:#e4aded !important;
	border:0 ;
	background-color:#a64fc2 !important;
	color:#8f587f !important;
	}
	#user_tt div a{
	color: #ffffff !important;
	}
	#user_tt .link a{
	color: #000000 !important;
	}
	#header .userinfo .shadow3 {
	background-color:#d7d4c5 !important;
	}
/*news*/
	#news div.body {
	background:none repeat scroll 0 0 #e4aded !important;
	}
	#news span {
	color:#666666 !important;
	}
.const .shadow3 { /*torrent search box, forum search box, bottom users/stats box*/
background:none repeat scroll 0 0 #ece9d8 !important;
}
#taggrid .grid tr{ /*not sure what these 2 do*/
background-color:#FCDCBF;
}
#taggrid .grid tr.alt{
background-color:#F2CBAD;
}
.grid-pager span { /*current page*/
background-color:#FFFFFF;
color:#333333;
}
.grid-pager a { /*other pages*/
background-color:#ece9d8 !important;
color:#b8716f;
}

/*torrent,forum grid*/
.breadcrumbtop a, .breadcrumbbottom a, .breadcrumb a{
color: #ffffff !important;
}
.grid  {
border:1px solid #000000 !important;
}
.grid th {
background-color:#a64fc2 !important;
color:#ffffff !important;
}
.grid td {
border-color:#000000 !important;
}

/*torrentlist*/

.grid tr{ background-color:#ece6e6 !important; }
.grid tr:hover { background-color: #fff6f6 !important; }
.grid tr.alt { background-color:#d7d4c5 !important; }
.grid tr.alt:hover {background-color: #e5c6c6 !important; }

.grid tr.snatched { background-color: #f4c3c3 !important;}
.grid tr.snatchedalt { background-color: #edb2b2 !important;}
.grid tr.snatched:hover { background-color: #f6cfcf !important;}
.grid tr.snatchedalt:hover { background-color: #ffcbcb !important;}

.grid tr.snatchedandposted { background-color: #e0ffe0 !important;}
.grid tr.snatchedandpostedalt { background-color: #DAF8DA !important;}
.grid tr.snatchedandposted:hover { background-color: #efffef !important;}
.grid tr.snatchedandpostedalt:hover { background-color: #E8F8E8 !important;}



/*forumlist*/
.forumtable td.group { 
background-color:#ffffff !important;
}
.forumtable td.topics, .forumtable td.posts { 
background-color:#ded8d8 !important;
}
.forumtable td.author, .forumtable td.lastpost, .forumtable td.delete {
background-color:#d5d0d0 !important;
}
.forumtable tr:hover td.topics, .forumtable tr:hover td.posts, .forumtable tr:hover td.views {
background-color:#ece6e6 !important;
}
.forumtable tr:hover td.author, .forumtable tr:hover td.lastpost, .forumtable tr:hover td.delete {
background-color:#ded8d8 !important;
}
.forumtable .info, .forumtable .info a.posted, .forumtable .info a {
color:#000000 !important;
}
.forumviewtable .info a {
color:#000000 !important;
}

/*forumthreads*/
.forumviewtable td.topics, .forumviewtable td.views {
background-color:#ded8d8 !important;
}
.forumviewtable td.author, .forumviewtable td.lastpost, .forumviewtable td.delete {
background-color:#d5d0d0 !important;
}
.forumviewtable tr:hover td.topics, .forumviewtable tr:hover td.views {
background-color:#ECE6E6 !important;
}
.forumviewtable tr:hover td.author, .forumviewtable tr:hover td.lastpost, .forumviewtable tr:hover td.delete {
background-color:#ded8d8 !important;
}
.forumviewtable tr.snatched td.topics, .forumviewtable tr.snatched td.views {
background-color:#F8DADA !important;
}
.forumviewtable tr.snatched td.author, .forumviewtable tr.snatched td.lastpost, .forumviewtable tr.snatched td.delete {
background-color:#F4C3C3 !important;
}
.forumviewtable tr.snatchedandposted td.topics, .forumviewtable tr.snatchedandposted td.views {
background-color:#DAF8DA !important;
}
.forumviewtable tr.snatchedandposted td.author, .forumviewtable tr.snatchedandposted td.lastpost, .forumviewtable tr.snatchedandposted td.delete {
background-color:#C3F4C3 !important;
}
.forumviewtable tr.snatched:hover td.topics, .forumviewtable tr.snatched:hover td.views {
background-color:#FFE5E5 !important;
}
.forumviewtable tr.snatched:hover td.author, .forumviewtable tr.snatched:hover td.lastpost, .forumviewtable tr.snatched:hover td.delete {
background-color:#F8DADA !important;
}
.forumviewtable tr.snatchedandposted:hover td.topics, .forumviewtable tr.snatchedandposted:hover td.views {
background-color:#E5FFE5 !important;
}
.forumviewtable tr.snatchedandposted:hover td.author, .forumviewtable tr.snatchedandposted:hover td.lastpost, .forumviewtable tr.snatchedandposted:hover td.delete {
background-color:#DAF8DA !important;
}


.forumviewtable td{
border:1px solid #000000 !important;
}
.threadviewgrid td {
border-bottom:5px solid #e4aded !important;
}
.threadviewgrid tr.bottom td {
border-bottom:0 none !important;
}
.threadviewgrid tr.bottom td.right {
border-top:1px solid #000000 !important;
}
/*actual post*/
	.grid td.left {
	background:none repeat scroll 0 0 #F8F3EB !important;
	border-top:1px solid #000000 !important;
	}
	.threadviewgrid .postinfo {
	background-color:#ffffff !important;
	border-right:0px !important;
	border:1px solid #000000 !important;
	color:black;
	}
	.grid td.right {
	background-color:#F8F3EB;
	border-bottom:1px solid #E0E0E0;
	color:black;
	}
	q  {
	background-color:#F2EBDF !important;
	border:1px solid #BBAAAA !important;
	}
	
#quickreply textarea { /*some space for the new border*/
width:98% !important;
}
.const ul.post { /*dunno*/
background-color:#F2EBDF !important;
border:1px solid #AAAAAA;
}
ul.profilenav { /*profile page*/
background:none repeat scroll 0 0 #d7d4c5 !important;
border:1px solid #AAAAAA;
}
ul.prefs  { /*preferences page*/
background:none repeat scroll 0 0 #F8F3EB !important;
border:0.1em solid black;
}
/*text colors*/
.const ul li label {/*form field labels, eg forum search*/
color:#7C3200  !important;
}
#header .nav li ul li a {
color:#7C3200  !important;
}
#header .nav a {
color:#FFFFFF  !important;
}

/* torrentview links */
td.c a {color:#8f587f   !important;}
td.r a {color:#8f587f   !important;}
td.name a {color:#000000   !important;}
span.tag a {color:#7b467c  !important;}
span.artist a {color:#7b467c  !important;font-weight: bold !important;}
.grid a.grid-ilink:hover{color:#BB0000  !important;}
.torrenttable th a {color:#ffffff  !important;}

a:hover {color:#0000CC !important;}

h1 {
border-bottom:1px solid #666666;
color:#003366 !important;
font-size:1.8em;
}
/*wiki*/
#p-navigation, #p-search, #p-tb  {
background:none repeat scroll 0 0 #F2EBDF !important;
}
#p-logo a,#p-logo a:hover{
background-image: url("http://dl.dropbox.com/u/30897/TMBlogo-red1.png") !important;
}



}
