css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
#channel-body {
  background-image: none !important;
  background-color: #EEEEEE !important;
}
#channel-body .outer-box {
  background-color: #DDDDDD !important;
}
html #channel-body .inner-box, html #channel-body .inner-box-colors, html #channel-body .inner-box-bg-color, #channel-body .inner-box-colors
{
  background-color: #FFFFFF !important;
}
.title-text, .title-text-color, a.title-text-color, .title-text-color a,
.outer-box .inner-box a, .outer-box .inner-box-colors a, .outer-box .inner-box-bg-color a, .outer-box .inner-box-link-color,
a.inner-box-color-as-link, .inner-box-colors .playnav-show .show-facets .show-mini-description, .inner-box-color, .inner-box
{
  color: #222222 !important;
}
.panel-tab-selected .panel-tab-indicator-arrow 
{
    border-bottom-color: #222222 !important;
}
}
