css:
@namespace url(http://www.w3.org/1999/xhtml);

/* userstyles */

@-moz-document domain("www.opencongress.org") {

/* bills */
#bill-text .overview_button span {color: black !important;}
.sidebar_recent_articles ul li .title,
.bill_text_section {color:black !important;}
.sidebar_recent_articles ul li .date,
a.small_button span {color: #F2117F !important;} /* pink on blue */
.title p {color: #AE2268 !important;} /* wine */
#login_writerep_trigger div .write {color: red !important;}
#users_result h3 {color: green !important;}
#users_result font {color: maroon !important;}

/* blogs */
.blog-date {color: #AE2268 !important;}
#header .bar .project,
.blog-body blockquote, p.tip,
.blog-body {color:black !important;}

#user_account a,
#header .bar .project a {color: #404000 !important;}

ul.recent_posts li a,
ul.tips li p a,
.blog-body p a {color: #000098 !important;}

ul.recent_posts li span,
ul.tips li p.small,
.blog_social_ref {color: #AE2268 !important;}
.blog_tag, .feed,
.blog_social_ref a {color: #265E15 !important;}

}
