css:
@-moz-document 
domain("aurorachan.net")
{
body{
   background: #2cad50 url(http://img204.imageshack.us/img204/3033/yotsubaclover.jpg) no-repeat fixed right bottom !important;
   color: #000000 !important;
   font-size: 12px !important;
}
td.reply{ 
   background: transparent url(http://img810.imageshack.us/img810/7854/clovercsspostblock.png) !important;
   border: 2px dashed #2cad50 !important;
   color: #000000 !important;
   font-family: Helvetica !important;
   -moz-border-radius: 4px !important;
   -webkit-border-radius: 4px !important;
}
blockquote { 
   font-family: Helvetica !important;
}
.unkfunc {
   color: #077F50 !important;
}
a {
   color: #EF000A !important;
}
a:hover {
   color: #000000 !important;
   background-color: #077F50 !important;
}
.omittedposts {
   font-family: Helvetica !important;
   font-size: 12px !important;
   color: #000000 !important;
}
.thumbnailmsg {
   color: #000000 !important;
}
.commentpostername, .postername {
   font-family: Helvetica !important;
   font-size: 12px !important;
   color: #077f50 !important;
}
.filetitle, .replytitle {
  color: #BF0085 !important;
}
.qrpostblock, .postblock {
   background: transparent url(http://img810.imageshack.us/img810/7854/clovercsspostblock.png) !important;
   color: black !important;
   border: 2px dashed #2cad50 !important;
   -moz-border-radius: 4px !important;
   -webkit-border-radius: 4px !important;
}
.highlight {
   background: #6FD9FF !important;
   border: 2px dashed #2cad50 !important;
   color: black !important;
}
.logo {
   color: #000000 !important;
}




.page_header ul {
   background: transparent url(http://img846.imageshack.us/img846/1816/cloverl.png) no-repeat bottom right !important;
   -moz-border-radius: 8px !important;
   -webkit-border-radius: 8px !important;
}
.page_header ul a {
   color: #000000 !important;
   font-size: 12px !important;
}
.page_header dt {
   background: transparent url(http://img810.imageshack.us/img810/7854/clovercsspostblock.png) !important;
   color: #000000 !important;
}
.page_header li {
   background: transparent url(http://img810.imageshack.us/img810/7854/clovercsspostblock.png) !important;
}




.menu {
   color: #000000 !important;
   -moz-border-radius: 8px !important;
   -webkit-border-radius: 8px !important;
   background: transparent url(http://img810.imageshack.us/img810/7854/clovercsspostblock.png) !important;z
}
.menu ul {
   border-right: 2px dashed #2cad50 !important;
   border-left: 2px dashed #2cad50 !important;
   background: transparent url(http://img846.imageshack.us/img846/1816/cloverl.png) no-repeat bottom right !important;
}
.menu li, .menu h2, .menu h1, .menu a {
   color: #000000 !important;
}
.menu h2, .menu h1 {
   margin-top: 0px !important;
   margin-bottom: 0px !important;
   border: 2px solid #2cad50 !important;
   background: transparent !important;
}
 .menu h1 {
   -moz-border-radius: 8px 8px 0 0 !important;
   -webkit-border-radius: 8px 8px 0 0 !important;
}
}
