/* "FACEBOOK Pink Style (fixed) + sticky top nav bar!" is based on the original Dark Style Release by ModStar, which was continued by VincentD and eventuelly completly fixed by Haiggoh. Credit for the static-top-bar hack go to Code.Red

*/





@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("facebook.com") 



{





#contentCol  { background-color:#fff3f6 !important; }

.profile .profile_color_bar  { background-color:#fff3f6 !important; }



#contentArea {

	color: #ff699e;

}



span.messageBody {

	color: #ff699e;

	text-shadow: #fffeff 0 1px 0 !important;

}



div.mall_post_body_text {color: #ff699e;

	text-shadow: #fffeff 0 1px 0 !important;

	

}



div.groupProfileHeaderWash {

	width: 789px;

	background-color: #ffc2ea;

}





/* navigation */

#blueBar       { background:#f0788c  !important; }

#pageLogo a    { background:url(http://www.madebymat.com/facebook/icons.png) no-repeat scroll -21px -3px !important; }

#headNavOut    { background:#f6b1c3 !important; border-color:#de264c !important; }

#content       { background-color:#ffdce8 !important;}

#headNav       { background-color:#ffc1d7 !important; border-color:#000 !important; }



#navItem_nf a.item {

	border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #f3c1d7 !important;

}



#navItem_messages {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #f3c1d7 !important;

	

}



#navItem_events {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #f3c1d7 !important;

	

}



#navItem_ff {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #f3c1d7 !important;

	

}



#q {color: #888 !important;

	}



#navItem_group_155611801140562 {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #ded3dc !important;

	

}



#groupSideNav ul.uiSideNav li {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #ded3dc !important;

	

}



#navItem_media {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #f3c1d7 !important;

	

}



#navItem_2347471856 {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #f3c1d7 !important;

	

}



#navItem_games {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #f3c1d7 !important;

	

}



#navItem_apps {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #f3c1d7 !important;

	

}



li.hidden {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #f3c1d7 !important;

	

}



#navItem_group_138742086177056 {border-top: 1px solid #f6b1c3 !important;

	border-bottom: 1px solid #f3c1d7 !important;

	

}



img.uiProfilePhoto.profilePic.uiProfilePhotoLarge.img {

	width: 40px;

	height: 40px;

	border: 2px inset #f6b1c3 !important;

	margin-left: 6px;

}



/* Control Remarks Color */

#home_stream, .ufiItem, .mall_divider_text {background: rgba(255,255,255,.3) !important;

}



/* Remove Borders & Lines */

* {border-style: none !important;

}



div.home_right_column {

	margin-right: 15px;

	margin-left: 15px;

}



div.ego_section {

	margin-right: 15px;

}



#event_create_mini_form div.mbs input.inputtext.event_name.DOMControl_placeholder {

	width: 215px;

}



/* main nav icons */

#jewelRequest { 

   background-image: url("http://www.madebymat.com/facebook/icons.png") !important;

   background-position: -25px -106.5px !important; }

#jewelMail { 

  background-image: url("http://www.madebymat.com/facebook/icons.png") !important;

  background-position: -25px -74px !important; }

#jewelNotif { 

  background-image: url("http://www.madebymat.com/facebook/icons.png") !important;

  background-position: -25px -138px !important; }

#jewelCase .jewel  { border:none !important; }

.jewelToggler:hover { background-color: #f6b1c3 !important; }





/* search */

#navSearch       { border-color:#f6b1c3 !important; }

#navSearch input { border-color:#f6b1c3 !important; }

#navAccountLink:hover { color:#f8edf2 !important; }





/* nav tabs */

#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#de264c !important; color:#ffffff !important; }





/* boxes */

.lightblue_box  { background-color:#ffd6e2 !important; border-color:#f6b1c3 !important; }

.commentable_item .ufi_section  { background-color:#ffd6e2 !important; }

textarea, .inputtext, .inputpassword  { border-color:#f6b1c3 !important; }





/* links */

a  { color:#de264c !important; }

.uiLinkButton input  { color:#2c2c2c !important; }

.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color:#2c2c2c !important; }

button.as_link  { color:#2c2c2c !important; }





/* side nav */

.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#f6b1c3 !important; }



.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:#fce1e8 !important; }

.friends_online_sidebar li.buddy_row a:hover  { background-color:#fce1e8 !important; }



.unread  { background-color:#f6b1c3 !important; }

.line  { color:#de264c !important; }





/* pop up box  */

#jewelCase .jewelFooter a   { background-color:#fce1e8 !important; }

#jewelCase .jewelFooter .uiTextSubtitle  { color:#de264c !important;  }



#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color:#de264c !important; }



#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#fce1e8 !important; border-color:#f0788c !important; }

#jewelCase .jewelBox  { background-color:#fff3f6 !important; }





/* copy */

.uiHeader h2  { color:#de264c !important; }





/* graffiti */

.current_stuff td  { background-color:#fff3f6 !important; border-color:#f6b1c3 !important; }









/* profile page */

.UIComposer .UIComposer_Box  { background-color:#fff3f6 !important; border-color:#f6b1c3 !important; }

.profile .box .box_header  { background-color:#ffcdd9 !important; border-color:#f6b1c3 !important; }







/* profile tabs */

.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ffffff !important; border-color:#f6b1c3 !important; }

.profile .top_bar ul.tabs li a.tab_link  { background-color:#f6b0c2 !important; border-color:#f0788c !important; }

.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#de264c !important; color:#ffffff !important; border-color:#de264c !important; }









/* chat */

#chat_tab_bar .disabled .chat_window .chat_conv  { background-color:#fff3f6 !important;  }

#chat_tab_bar .chat_header  { background-color:#f0788c !important; border-color:#000000 !important; }

#chat_tab_bar .chat_header_name a  { color:#ffffff !important; }

#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color:#f6b1c3 !important; color:#de264c !important; }

#presence_bar .presence_menu_opts h2 span  { background:#f0788c !important; }

#presence_bar .presence_menu_opts h2  { border-color:#f0788c !important; }

.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background:#fff3f6 !important; }



#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#f0788c !important; border-color:#f0788c !important; }

#presence .presence_bar_button .inner_button  { border-top:none !important; }

#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ffffff !important; }



#presence .presence_menu_opts  { border-color:#f0788c !important; }







/* buttons */

.UIButton_Blue  { background:#de264c !important; border-color:#f0788c !important; }

.UIComposer_InputArea  { border-color:#f6b1c3 !important; }

.uiButtonConfirm  { background:#de264c !important; border-color:#f0788c !important; }

#presence #buddy_list_tab #buddy_count strong  { color:#ffffff !important; }



}



/* The following is modified code from "Sticky Top Nav Bar & More Prettiness" by Code.Red

* Thank you for creating this awesome modification!

*/





/* 

  If Facebook ever breaks this style, check

  http://userstyles.org/styles/25094

  as I will probably have uploaded a fixed version.

  Thanks! -Code.Red



  This version updated January 4 2011, 21:42

*/



@-moz-document domain("facebook.com") {



/* START sticky nav bar */

#globalContainer #pageHead, .loggedout_menubar  {

  width: 981px !important;

  left: 50%;

  margin-left: -490.5px !important;

}

#pageHead {

  position: fixed !important;

  width: 981px !important;

  margin: 0 auto !important;

  top: 0px;

  z-index: 15 !important;

}

#blueBar {

  position: fixed !important;

  top: 0px;

  width: 100%;

  visibility: visible;

  -webkit-box-shadow: 0px 2px 8px rgba(0,0,0,0.5);

  -moz-box-shadow: 0px 2px 8px rgba(0,0,0,0.5);

  z-index: 14 !important;

}

#content {

  padding-top: 35px !important;

}

body.UIPage_LoggedOut #content {

  padding-top: 0 !important;

}

.loggedout_menubar {

  position: fixed;

}

/* END sticky nav bar */





/* START sticky left column on main page */

body.ego_page #mainContainer > #leftCol {

  position: fixed;

  top: 35px;

}

/* END sticky left column on main page */





/* START move log out button to nav bar */

li#navAccount:not(.openToggler) > ul > li:last-child {

  position: absolute;

  left: 50%;

  width: 72px;

  margin-left: 112px;

  top: -30px;

  visibility: visible !important;

}

li#navAccount:not(.openToggler) > ul > li:last-child input {

  border: 0 !important;

  color: #ad6fa3 !important;

  font-weight: bold !important;

  height: 30px !important;

  padding-top: 4px !important;

}

li#navAccount:not(.openToggler) > ul > li:last-child input:hover{

  color: #FFF !important; background-color: #343434 !important;

  padding-bottom: 5px !important;

}

li#navAccount ul {

  display: block !important;

  visibility: hidden;

}

#navAccount.openToggler ul {

  visibility: visible;

}

/* END move log out button to nav bar */





/* START blink new notifications buttons */

.jewel { margin-right: 1px !important; }

.jewelNew {

  background: url('data:image/gif;base64,R0lGODlhAQABAIAAAP///zpalCH/C05FVFNDQVBFMi4wAwEAAAAh+QQJeAABACwAAAAAAQABAAACAkQBACH5BAl4AAEALAAAAAABAAEAAAICTAEAOw==') !important;

  -moz-border-radius: 6px;

  /*border: solid 1px white !important;*/

  display: block;

  height: 29px !important;

}

/* END blink new notifications buttons */





/* START round the top edges */

#headNav, .jewel, .jewelToggler, #pageLogo > a {

  -webkit-border-top-left-radius: 4px;

  -webkit-border-top-right-radius: 4px;

  -moz-border-radius-topleft: 4px;

  -moz-border-radius-topright: 4px;

}

#pageNav #navAccount > a, li.openToggler {

  -webkit-border-top-left-radius: 5px;

  -webkit-border-top-right-radius: 5px;

  -moz-border-radius-topleft: 5px;

  -moz-border-radius-topright: 5px;

}

/* END round the top edges */





/* START shadows under menus & chat */

#navAccount > ul, .jewelBox, .uiTypeaheadView, .uiOverlay, .pop_container_advanced {

  -webkit-box-shadow: 3px 3px 20px rgba(0,0,0,0.5);

  -moz-box-shadow: 3px 3px 20px rgba(0,0,0,0.5); }



#fbDockChat > div, .fbNubFlyout, .uiMenu  {

  -webkit-box-shadow: 0px 0px 12px rgba(0,0,0,0.5);

  -moz-box-shadow: 0px 0px 12px rgba(0,0,0,0.5); }

/* END shadows under menus & chat */





/* START fix top light blue banner (set home page reminder, etc) */

#ConfirmBannerOuterContainer {

  position: relative !important;

  top: 40px;

}

/* END fix top light blue banner */







/* START CSS3 animations */

@-webkit-keyframes slidefromtop {

from {

  opacity: 0;

  -webkit-transform: translateY(-10px);

}}

@-moz-keyframes slidefromtop{

from {

  opacity: 0;

  -moz-transform: translateY(-10px);

}}

.jewelBox, .uiSelectorMenuWrapper, .uiTypeaheadView{

  -webkit-animation: slidefromtop 0.3s;

  -moz-animation: slidefromtop 0.3s;

}



@-webkit-keyframes slidefrombottom {

from {

  opacity: 0;

  -webkit-transform: translateY(10px);

}}

@-moz-keyframes slidefrombottom {

from {

  opacity: 0;

  -moz-transform: translateY(10px);

}}

.fbNubFlyout, .hovercard, .uiTooltipWrap {

  -webkit-animation: slidefrombottom 0.3s;

  -moz-animation: slidefrombottom 0.3s;

}



@-webkit-keyframes slidefromtoplarge {

from {

  opacity: 0;

  -webkit-transform: translateY(-200px);

}}

@-moz-keyframes slidefromtoplarge {

from {

  opacity: 0;

  -moz-transform: translateY(-200px);

}}



.pop_dialog {

  -webkit-animation: slidefromtoplarge .6s;

  -moz-animation: slidefromtoplarge .6s;

}



/* END CSS3 animations */



}
