css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("justin.tv") {
.wrapper, .hdr_inside
{
  width: 1250px !important;
}
.left_col
{
  width: 920px !important;
}
#info .content_container
{
  width: 860px !important;
  margin-left: 50px !important;
}
#banner_custom
{
  float: right;
  height: 30px !important;
  padding-top: 4px !important;
  margin-right: 10px !important;
  margin-bottom: -50px !important;
}
#info
{
  margin-top: 0px !important;
}
#info .content_container .content 
{
    min-height: 44px !important;
}
#channel_status_message
{
  font-size: 0.8em !important;
}
#standard_holder
{
}
#banner_custom h1 img
{
  height: 3em;
}
#banner_default h1
{
  font-size: 1em !important;
  line-height: 1em !important;
  width: 858px !important;
  background: none !important;
  text-align: right !important;
  padding-right: 4px !important;
}
#banner_default
{
  height: 0em !important;
  float: right;
}
#info .content_container 
{
}
#info .info_pic, #info .info_pic a img
{
    height: 46px !important;
    width: 46px !important;
}
.live_site_player_container, #live_site_player_flash
{
  width: 910px !important;
  height: 550px !important;
}
.ad #chat_lines, .promo #chat_lines
{
  height: 530px !important;
}
#admin_nxtchan
{
}
#ad_holder
{
  display: none !important;
}
}
