css:
#navigator-toolbox:not([tabsontop="true"]) toolbar{
background:rgba(255,255,255,0) !important;
}

#navigator-toolbox:not([tabsontop="true"]) #nav-bar {
max-height:31px ;
overflow:hidden ;
margin:-1px 0px 0px 0px;
padding:4px 0px 1px 0px !important;
}

#navigator-toolbox:not([tabsontop="true"]) #toolbar-menubar{
margin: -22px 0px 22px 125px ;
}

#navigator-toolbox:not([tabsontop="true"]) #PersonalToolbar{ 
max-height:22px ;
margin: -54px 115px 31px 125px !important;
}

#navigator-toolbox:not([tabsontop="true"]) #TabsToolbar{ 
height:27px ; 
margin: -25px 0px 0px 555px  ; 
z-index:-10 ;
}

#navigator-toolbox:not([tabsontop="true"]) #stumbleupon{
margin:-27px 0px 25px 555px !important;
}

#navigator-toolbox:not([tabsontop="true"]) tab:not([selected="true"]) {
opacity:0.75;
}

#navigator-toolbox:not([tabsontop="true"]) tab[selected="true"] { 
opacity:0.9 ;
}


#navigator-toolbox:not([tabsontop="true"]) .tabs-newtab-button{ opacity:0.75 ;
}
 
#appmenu-button-container {
background-image: -moz-linear-gradient(rgb(247,182,82), rgb(215,98,10) 95%) !important;
background-clip: padding-box !important;
border-radius: 2px 2px 6px 6px !important;
border-top: none !important;
}

#appmenu-button {
background-image: url("http://people.mozilla.com/~faaborg/files/shiretoko/firefoxIcon/firefox-48-noshadow.png") !important;
background-repeat: no-repeat !important;
-moz-border-radius: 0 0 4px 4px !important;
}

#main-window[privatebrowsingmode] #appmenu-button-container {
background-image: -moz-linear-gradient(rgb(153,38,211), rgb(105,19,163) 95%) !important;
border-color: rgba(43,8,65,.9) !important;
}

#navigator-toolbox:not([tabsontop="true"]) #browser {
box-shadow: 0px -10px 12px -12px #666 !important;
}

#navigator-toolbox:not([tabsontop="true"]) #back-button{
-moz-transform:scale(1.15);
margin-left:7px !important;

}

#navigator-toolbox:not([tabsontop="true"]) #forward-button{
margin-left:-2px !important;
margin-right:0px !important;
margin-bottom:0px !important;
}

#navigator-toolbox:not([tabsontop="true"]) #home-button{
margin-right:0px !important;
margin-bottom:0px !important;
} 

#navigator-toolbox:not([tabsontop="true"]) #urlbar{ 
max-width:450px ;
margin-bottom:0px !important;
}


#TESTTOCHECK {}
