@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(translation.babylon.com){

/* hide popup */
#popup-wrapper{
    display:none !important;
}

/* hide human translation button */
.humanTranslateButton{
    display:none !important;
}

/* hide header */
div.mainContantHeader, div.wrapheader{
    display:none !important;
}

/* hide featured translation */
#bottomPart-wrapper, #featured, #bottomADV, .mainContant-wrapper > h3 {
    display:none !important;
}

/* hide footer */
#fotter-Wrapper{
    display:none !important;
}

/* move content to top*/
form#translation-wrapper {
    position: absolute;
    top: 0;
}

}
