css:
/* ========================== server settings ============================================== */
@-moz-document domain("runeriders.com"),domain("m109riders.com"),domain("furyriders.com"),
  domain("bigbikeriders.com"),domain("vtxoa.com"),domain("gl1800riders.com") 
/* ========================== end server settings ========================================== */

/* ============================= Illusion Blue ============================================= */
{ 

/* common settings */

.logo-image,
.below_body,
img[src="http://gl1800riders.com/forums/sponsors/dlpgl1800.gif"],
img[src="http://gl1800riders.com/forums/images/misc/gl1800-logo.gif"],
img[src="http://www.runeriders.com/forum/images/misc/vbulletin3_logo_white.gif"],
img[src="http://gl1800riders.com/forums/images/misc/vbulletin3_logo_white.gif"],
img[src="http://www.bigbikeriders.com/forum/images/misc/vbulletin3_logo_white.gif"],
img[src="http://www.vtxoa.com/forums/images/misc/vbulletin3_logo_white.gif"],
img[src="http://www.furyriders.com/forums/images/misc/vbulletin3_logo_white.gif"],
div[id="ad_global_below_navbar"] {
    display: none !important;
}
.body_wrapper,
div [class="page"] {
    background: -moz-linear-gradient(top left 40deg, #000000, #cccccc)!important;
    border: 1px solid #FFE081;
}
table {
    border: #000000 !important;
}
.wysiwyg {
    background-color: #ffffff!important;
    color: #000000!important;
}
img[src="http://www.runeriders.com/forum/images/misc/vbulletin3_logo_white.gif"], div [class="trc_rbox_container"] {
    display: none !important;
}
div [class="page"] {
    background: -moz-linear-gradient(top left 40deg, #000000, #cccccc)!important;
    border: 1px solid #FFE081;
}
table {
    border: #000000 !important;
}
.wysiwyg {
    background-color: #ffffff!important;
}
.breadcrumb a:visited {
    color: #FFE081 !important;
}
a[class="popupctrl"] {
    color: #000000 !important;
}
li[class="popupmenu nonotifications"] {
    background-color: #ffffff !important;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
}
td[class="blocksubhead"] {
    color: #ffffff !important;
}
/* theme spacific settings */
/* menu items */

body {
    background-color: #040761!important;
    color: #ffffff !important;
}
.popupbody {
    background-color: #040761 !important;
    border: 1px solid #FFE081!important;
}
.popupmenu li a {
    background-color: #040761 !important;
    color: #FFE081 !important;
    border: 1px solid #FFE081!important;
}
ul[class="forumstats td"] {
    color: #000000 !important;
}
a div[class="breadcrumb"] {
    color: #FFE081 !important;
}
/* tables & tabs */

tborder {
    background-color: #040761!important;
}
.tfoot,
.forumtitle,
.posthead,
.forumhead,
.pagetitle,
.searchlisthead {
    background-color: #040761!important;
    color: #ffffff !important;
    padding: 3px !important;
    border: #FFE081 !important;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
}
.tcat {
    background-color: #040761 !important;
    border: 1px solid #FFE081 !important;
}
td [class="tcat"] {
    background-color: #040761 !important;
}
div[class="siderow"],
.sidebody {
    background-color: #040761 !important;
    border: 1px solid #FFE081!important;
}
tborder {
    background-color: #040761!important;
}
form[class="pagination popupmenu nohovermenu"] {
    background-color: #040761!important;
}
.popupctrl,
.prev_next a,
.first_last a {
    color: #040761!important;
}
.thead,
.blockhead,
div[class="forumhead"],
div[class="blockfoot floatcontainer"],
div[class="siderow"],
.sidebody,
.simpleday,
.blocksubhead {
    background-color: #040761!important;
    border: 1px solid #FFE081!important;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
}
/* calendar */

.othermonth {
    background-color: #cccccc !important;
    border: 1px solid #000000 !important;
}
h3 span[class="blocksubhead dayname"],
th[class="blocksubhead"],
td[class="today"] {
    color: #FFE081 !important;
}
h3 span[class="daynum"],
div[id="view-cat_itrader_stats"] {
    color: #000000 !important;
}
dd[class="selected"],
dd[class="userprof_module"],
div[class="memberprofiletabunder"] {
    background-color: #040761!important;
    border: transparent !important;
}
dd a[class="userprof_moduleinactive"] {
    color: #000000 !important;
}
h3.threadtitle a {
    color: #040761 !important;
}
h5,
h3.blocksubhead,
h4[class="subsectionhead-understate"] {
    color: #ffffff !important;
}
span[class="selected"] {
    background-color: #040761 !important;
}
/* page layout */

div[class="eventhead"],
div[class="forumhead foruminfo L1 collapse"],
div[class="threadlisthead table"],
div[class="floatcontainer footer"] {
    background-color: #040761!important;
    border: #FFE081!important;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
}
.threadtitle a {
    color: #ffe081 !important;
}
div[id="pagetitle"],
.pagetitle {
    background-color: transparent !important;
    color: #ffffff !important;
}
.threadpagestats,
.above_threadlist_controls,
.thread_controls {
    background-color: transparent !important;
    color: #ffffff !important;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
}
div[class="mediahead"],
.sidehead {
    background-color: #040761 !important;
    border: #040761 1px !important;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
}
td [class="thead"] {
    background-color: #040761!important;
}
h1,
.toolsmenu,
.lastnavbit,
.separator,
.selected a,
.sidebody a,
.siderow a {
    color: #ffe081 !important;
}
a.active [class="navtab"] {
    background-color: #040761!important;
}
.forumtitle a {
    color: #ffffff!important;
}
div[id="albumtop"] {
    background-color: #040761!important;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
}
/* ===================== end ============================ */
}
