css:
@-moz-document domain('highdeas.com') {

body {
  background-image: none !important;
  background-color: #2a2a2a !important;
  color: #CCC !important;}
p {
  -webkit-margin-before: 0px !important;
  -webkit-margin-after: 0px !important;}
a:link {color: rgb(55, 136, 57) !important;}
#logo {
  margin-left: 40px !important;
  overflow: hidden !important;}
#logo img {
  margin-top: -30px !important;
  margin-bottom: -30px !important}
#header {height: 74px !important;}
#header-wrapper {background-position: 0 !important;}
#block-menu-menu-highdeas ul li.active-trail ul li a {
  background-color: #2a2a2a !important;
  color: #CCC !important;
  text-shadow: none !important;}
#block-menu-menu-highdeas ul li.active-trail a {
  height: 30 !important;
  padding-bottom: 0px !important;
  background-image: none !important;
  background-color: #333 !important;
  color: rgb(55, 136, 57) !important;
  text-shadow: none !important;}
#block-menu-menu-highdeas ul li {
  margin-bottom: 35px !important;}
#block-menu-menu-highdeas ul li a {
  text-shadow: none !important;
  color: rgb(36, 91, 37) !important}
#block-menu-menu-highdeas ul {background-image: none !important;}
#content,
.region.region-sidebar-second.column.sidebar
  {margin-top: 0px !important;}
#navigation-wrapper {
  background-image: none !important;
  background-color: #333 !important;
  height: 31px !important;}
#navigation li {padding-top: 0px !important;}
#block-views-highdeas_popular-block_top_48h,
#block-block-18,
.block.even,
.block.even,
#block-tagadelic-2 .content {
  background-image: none !important;}
.region-sidebar-second .section {background-color: #333 !important;}
.region-sidebar-second {float: left !important;}
.highdea-entry-wrapper {margin-bottom: 0px !important;}
.highdea-entry-wrapper .bottom {
  background-image: none !important;
  height: 10px !important;
  padding: 0px !important;
  margin-bottom: 10px !important;}
.highdea-entry-wrapper .background {
  padding-top: 0px !important;
  padding-left: 40px !important;
  padding-right: 20px !important;
  padding-bottom: 0px !important;
  width: 600px !important;
  background-image: none !important;
  background-color: #333 !important;
  padding-bottom: 10px !important;}
.highdea-entry-wrapper .comments {
  background-image: none !important;
  background-color: #3d3d3d !important;
  bottom: 0px !important;
  padding: 0px !important;
  right: 40px !important;}
.highdea-entry-wrapper .comments a {
  font-weight: bold !important;
  text-shadow: none !important;
  vertical-align: middle !important;}
.highdea-entry-wrapper .title a {max-width: 520px !important;}
.vud-widget-hdvnode {
  height: 70px !important;
  width: 45px !important;}
.vud-widget-hdvnode .hdvnode-score.popular,
.pop-48 .votes {
  background-image: none !important;
  background-color: rgb(36, 91, 37) !important;
  text-shadow: none !important;
  color: #CCC !important;}
.vud-widget-hdvnode .hdvnode-score,
.upcoming-block .votes {
  background-image: none !important;
  background-color: #3d3d3d !important;
  text-shadow: none !important;
  color: #CCC !important;}
.highdea-entry-wrapper .name a {color: rgb(36, 91, 37) !important}
div > [class="top"] {display: none !important;}
.region-sidebar-second .block .title {
  background-image: none !important;
  width: 235 !important;
  border-bottom: 2px solid #3a3a3a !important;
  margin-left: 10px !important;
  margin-right: 10px !important;
  color: #aaa !important;}
.pop-48 .highdea a,
.upcoming-block .highdea a {
  text-shadow: none !important;}
.item-list .pager li a {color: #CCC !important;}
.highdea-entry-wrapper .body-bg {
  background-image: none !important;
  background-color: #333 !important;
  padding: 10px 5px !important;}
.highdea-entry-wrapper .meta,
.highdea-entry-wrapper .body-bg .content {
  background-image: none !important;
  border-top: 1px solid #3a3a3a !important;
  padding: 10px 0px !important;}
.highdea-entry-wrapper .body-bg .vud-widget {margin-left: 20px !important;}
.highdea-entry-wrapper .body-bg .submitted {
  padding-bottom: 15px;
  padding-left: 50px;}
.highdea-entry-wrapper .field-field-promoted-on {top: -20px !important;}
.highdea-entry-wrapper .body-bg .vud-widget {
  left: -95px !important;
  top: -95px !important;}
ul.primary,
ul.primary li.active a .tab,
ul.primary li a .tab {
  background: none !important;}
ul.primary li.active a {
  background-image: none !important;
  background-color: #333 !important;
  color: rgb(55, 136, 57) !important;}
ul.primary li a {
  background-image: none !important;
  color: rgb(36, 91, 37) !important;}
div.tabs {margin-bottom: 10px !important;}
.comment .comment-picture {
  background-image: none !important;
  background-color: #3a3a3a !important;}
.comment .comment-background {
  background-image: none !important;
  background-color: #333 !important;}
.comment .comment-bottom {
  background-image: none !important;
  background-color: #333 !important;
  height: 12px !important;}
.indented .comment .comment-background,
.indented .comment .comment-picture,
.indented .comment .comment-bottom {
  background-color: #2a2a2a !important;}
.comment.comment-by-node-author .comment-picture {
  background-color: rgb(36, 91, 37) !important;}
.resizable-textarea .grippie {
  background-color: #2a2a2a !important;
  border-color: #3a3a3a !important;}
#comments #edit-preview {color: #AAA !important;}
.tagging-curtags-wrapper {background-color: #2a2a2a !important;}
fieldset {border-color: #3a3a3a !important;}
.node-form .form-text,
.node-form textarea {
  background-color: #333 !important;
  border-color: #3a3a3a !important;
  color: #CCC !important;}
textarea {margin-left: 0px !important;}
#page-wrapper, .region-page-closure {
  width: auto !important;
  margin-left: 20px !important;
  margin-right: 20px !important;}
.region.region-sidebar-second.column.sidebar {
  float: left !important;
  right: 0px !important;}
#content .section,
.sidebar-second #content {
  width: auto !important;
  float: left !important;
  min-width: 712px !important}

    
.highdea-entry-wrapper .facebook,
.left-quote,
.right-quote,
div > [class="block block-block region-odd odd region-count-5 count-5"],
div > [class="block block-search first region-odd odd region-count-1 count-1"]
  {display: none !important;}

}
