css:
@-moz-document domain('www.fuska.nu'), url-prefix('http://fuska.nu') {
body, div.sellbuy form select#typ, div.sellbuy form select#stad, div.sellbuy form select#kategori, .maincontent .leftcontent div.memberoptions, .maincontent .leftcontent div.memberoptions div.left, .maincontent .leftcontent div.memberoptions div.right, .maincontent .leftcontent div.memberoptions div.right a, .multisearch {
	background: #ffffff !important;
	color: #000000 !important;
}

div.sellbuy form select#typ, div.sellbuy form select#stad, div.sellbuy form select#kategori option[style="color: #FFF"], .list1 td, .list2 td {
	color: #000000 !important;
}

.adright, .maincontent .leftcontent .ad {
	background: #f0f0f0 !important;
	border: solid 1px black !important;
}

.topbar {
	background: #76281E  !important;
	color: #C5D3D6  !important;
	border-bottom: solid 1px black  !important;
}

.topbar li ul {
	background: #ffffff !important;
	border: solid 1px black !important;
}

.topbar li ul li a:hover {
	color: #c0c0c0 !important;
}

.maincontent .leftcontent .list .listcontent .text div[style="color: #CCC;border-top: 1px solid #BBB;margin: 0;"] {
	color: #606060 !important;
}

.topbar >li {
	margin-top: 3px !important;
}

.wraper .topbox {
	background: #76281E !important;
	height: 100px !important;
}

.wraper .topbox .tinymenu {
	position: relative !important;
	top: 8px !important;
	font-weight: bold !important;
}

.wraper .topbox .tinymenu ul li {
	margin-right: 18px !important;
}

.fb_share_count fb_share_count_right, .fb_share_count_inner {
	display: none !important;
}

.wraper .topbox .searchbox {
	background: #76281E !important;
	position: relative !important;
	left: 80px !important;
}

.wraper .topbox .loginbox {
	background: none !important;
	color: #C5D3D6 !important;
	position: relative !important;
	left: -30px !important;
}

.wraper .topbox .loginbox .logged_in a {
	color: #C5D3D6 !important;
}

#logo {
	position: absolute !important;
	top: 0px !important;
	left: 0px !important;
	width: 129px !important;
	height: 41px !important;
}

#logo a {
	background: url("http://img801.imageshack.us/img801/1627/logontv.png") !important;
	width: 124px !important;
	height: 41px !important;
	position: absolute !important;
	top: 0px !important;
	left: 5px !important;
	z-index: 3 !important;
}

#logo img {
	display: none !important;
}

.maincontent .leftcontent div.memberoptions div.right a:hover {
	text-decoration: underline !important;
}

.menu {
	background: url("http://img251.imageshack.us/img251/5723/menubad.png") !important;
}

.maincontent {
	background: url("http://img856.imageshack.us/img856/4118/contentbg.png") !important;
	margin-left: 8px !important;
}

.maincontent .leftcontent .list .listcontent, .maincontent .right .list .listcontent, .maincontent .leftcontent .list .listcontent ul li.small:hover, ul.edit, .msg_popupcontent, .list2, .maincontent .leftcontent_wide .list .listcontent {
	background: #f0f0f0 !important;
}

.maincontent .right .list .listcontent ul li:hover, .maincontent .leftcontent .list .sellbuy_list tr:hover, .maincontent .leftcontent .list .sellbuy_list_header, .maincontent .leftcontent .list .listcontent ul li .nr, .list1, .maincontent .leftcontent div.game div#information div.nr, .maincontent .leftcontent .list .listcontent div.comment textarea, .maincontent .leftcontent div.game div#information div.xp, .tmlquotetable, input[name="amne"], textarea[name="text"], div[style="background: #222 !important; border: 1px solid black !important;color: #DDD !important;"] {
	background: #c0c0c0 !important;
}

.list1_over, .list2_over, .maincontent .leftcontent .list .listcontent div.tips_and_trix_controller {
	background: #a0a0a0 !important;
}

.maincontent .right .list .listcontent ul li h1 a, .maincontent .right .list .listcontent ul li .console a, .maincontent .leftcontent .list .sellbuy a, .maincontent .leftcontent .list .listcontent ul li h1 a, .maincontent .leftcontent .list .listcontent ul li p a, #footer a, .maincontent .leftcontent .list .listcontent a, .ljus, .maincontent .right .list .listcontent a, .maincontent .leftcontent .list h2 a, .sidan a, ul.edit li a, .msg_popupcontent a {
	color: #222222 !important;
}

.maincontent .right .list .listcontent ul li .username a {
	color: #ff0000 !important;
}

.maincontent .leftcontent .list .header, .maincontent .header, .rubrik, .under_rubrik, .maincontent .leftcontent .list .headerarchive, #gameOptions ul li a, .maincontent .leftcontent .list .headertipsandtrix, .maincontent .leftcontent .list .listcontent .links a, .maincontent .leftcontent .list .listcontent .buttonLinks a, .maincontent .leftcontent .list .headercomment, .msg_popup .header, .maincontent .leftcontent .list .sellbuy .pagination ul, .maincontent .leftcontent .list .archivesearch .pagination ul, .maincontent .leftcontent .list .headerpcgame, .under_rubrik2 {
	color: #ffffff !important;
}

.wraper .mainbottom {
	background: url("http://img860.imageshack.us/img860/7671/bottombg.png") !important;
}

#footer {
	background: url("http://img43.imageshack.us/img43/9348/footersy.png") no-repeat !important;
	background-position: center !important;
	height: 40px !important;
	margin: 0px 0px 5px 17px !important;
}

.maincontent .leftcontent .list .listcontent ul li.small .nr, .maincontent .leftcontent .list .listcontent ul li .nr, .maincontent .leftcontent div.game div#information div.nr, .maincontent .leftcontent .list .listcontent div.comment textarea, .maincontent .leftcontent div.game div#information div.xp, .topbar li ul li a, input[name="amne"], textarea[name="text"], div[style="background: #222; border: 1px solid black; color: #DDD;"] {
	color: #000000 !important;
}

.maincontent .leftcontent .list .reply {
	border: groove 2px gray !important;
	padding: 3px !important;
	background: #f0f0f0 !important;
}
}
