css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/blog/frostednightlock"), url-prefix("http://www.tumblr.com/inbox") {
#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members, body{
background-image:url('http://i923.photobucket.com/albums/ad75/shena711/Seamles-Red-89.jpg') !important;
background-repeat:repeat !important;
background-attachment: fixed;
background-color: #ffffff;
background-position:top !important;
color: #e8b6b5 !important;
font-family: Calibri;
}



#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members div.l-content, #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color: #ffffff;
color: #e8b6b5 !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: #ffffff !important; color:#e8b6b5 !important;}

#dashboard_edit_post{
background-color: transparent;
 font-family: Calibri; 
color: #e8b6b5 !important;
}



#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
background: none;
color: #e8b6b5 !important;
}

#right_column .dashboard_nav_item {
background: none;
background-attachment: initial;
background-position-x: 30%;
background-position-y: 100%;
background-origin: initial;
background-clip: initial;
background-color: #efa061;
}

#right_column .dashboard_nav_item #hide_radar {
background-image: none;
background-repeat-x: no-repeat;
background-repeat-y: repeat;
background-attachment: initial;
background-position-x: 0%;
background-position-y: 0%;
background-origin: initial;
background-clip: initial;
background-color: #efa061;
}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background: rgba(0,0,0,0.5);
}

#left_column #posts .notification .avatar {
                        border-color:transparent !important;
                        background-color:#ffffff !important;
 font-family: Calibri; color: #e8b6b5 !important;

                    }



#left_column #posts .notification .avatar:hover {opacity:1 !important;}

.post{opacity:1 !important; border-bottom:0px !important; color:#e8b6b5; font-size:11px; font-family: Calibri;}
.post_title {font-family: Orator std !important; font-size:16px !important; color:#e8b6b5;}
.post:hover{opacity:1 !important}
.post_info, .post_controls, .nav_item, a, a:active, a:visited {color:#e8b6b5 !important; text-decoration:none;  font-family: Calibri;}
.post_info {color:#e8b6b5 !important;}
.permalink{background-color: #e8b6b5 !important; background-image:none !important;  font-family: Calibri;}
.active{background-color:#e8b6b5 !important;  font-family: Calibri;}
.nav_item_nipple{display:none !important;  font-family: Calibri;}
.nav_menu{background-color:#efa061 !important; border-bottom:0px !important; color:#e8b6b5 !important;  font-family: Calibri;}


#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#efa061 !important;
background-image:none !important;
border:0px !important;
color:#e8b6b5 !important;
font-family: Calibri;
}



#nav .nav_item .nav_menu {
border-bottom:0px !important;
 font-family: Calibri;
color: #e8b6b5 !important;
}



#nav .nav_item .nav_menu a {
color: #e8b6b5 !important;
border-top-color:#ffffff !important;
font-family: Calibri;
}

#nav .nav_item .nav_menu a:hover {
color:#e8b6b5 !important;  font-family: Calibri;}


.dashboard_switch_blog_menu_header{
background-color:#db8e8e !important;
color: #e8b6b5 !important; !important;
}

.dashboard_switch_blog_menu_item{
background-color:#ffffff !important;
color:#e8b6b5 !important;
border:0px !important
 font-family: Calibri;
}

.dashboard_switch_blog_menu_footer{
background-color:#ffffff !important;
color:#e8b6b5 !important;
 font-family: Calibri;
}

.dashboard_switch_blog_menu_wrapper{
background-color:#ffffff !important;
color: #e8b6b5 !important;
}



#new_post_notice_container{
color:#fff !important
}



#new_post_notice_container a{
color: #e8b6b5 !important; !important
}



#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important;  font-family: Calibri; color: #e8b6b5 !important;}


#footer{
background: #ffffff !important;
background-image:none !important
}


ol#posts li.post.new_post {
background: #ffffff url('http://assets.tumblr.com/images/new_post_icons_no_labels.png') center center no-repeat;
}



img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 0;
}

.new_post_label{
font-size:12px !important;
padding-top:5px !important;
font-family:orator std !important;
text-transform: none !important;
color:#db8e8e !important;
letter-spacing:1px !important
}

#left_column #posts .notification {
                        background-color:#ffffff !important;
                        color:#db8e8e !important;
                        border-bottom-color: #db8e8e !important
                    }



#left_column #posts .notification .block {
                            color:#db8e8e !important;
                        }



#left_column #posts .notification.alt {
                        background-color: #ffffff !important;
border-bottom-color: #db8e8e !important
                    }

#left_column #posts .notification.last_notification,
                    #left_column #posts .notification.single_notification {
                        border-bottom-color:#db8e8e !important;
                    }

#left_column #posts .notification a {
                        color:#db8e8e !important;
                        border-bottom:0px !important;
                    }

#left_column #posts .notification blockquote {
                        border-left-color:#eee !important;
                    }



#left_column #posts .notification .avatar {
                        border-color:transparent !important;
                        background-color:#fbfbfb !important;
                    }



#left_column #posts .notification .avatar:hover{
                    }
.notification_type_icon{display:none !important}


h1,h2,h3,h4,h5,h6{ font-family: Calibri !important; color: #e8b6b5 !important;}


#slug_url{
color:#e2b6b7 !important}

.last_updated{
color:#e2b6b7 !important}

span.action{
color:#e2b6b7 !important}

span.control{
color:#e2b6b7 !important}

small{
color:#e2b6b7 !important}

.hide_overflow{
color:#e2b6b7 !important}

span.count{
color:#e2b6b7 !important}

span.quote{
color:#e2b6b7 !important}

.source{
color:#e2b6b7 !important}

.label{
color:#e2b6b7 !important}

#edit_post_controls #slug_url .inline_text_field{
color:#e2b6b7 !important}

}
