@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mytv.kz") {
}
body {
background:none!important;
}

html, #cont{
background:none!important;
margin-top:20px;
}
#site{
	width:100%!important;
}


#llogin, #rcol, #footer, #mg_container, #MarketGid773, #ratig-layer, .yashare-auto-init, #wrp{
 display:none!important;
}

.blocknews
{
border:1pt solid silver!important;
display:none;
}

.blocknews h2,#lcol .block h2
{
background: -moz-radial-gradient(75% 50%, circle cover, #6666CC, #9999FF, #CCCCFF, #fff)!important;
}

#dle-content .blocknews, #lcol .block {
display:block!important;
-moz-border-radius: 1em;
border:1px solid silver;
-moz-box-shadow: 1px 1px 6px rgba(20,20,20,51);
}

#dle-content .blocknews a, #lcol .block a{
}

#dle-content .navigation a{
-moz-border-radius: 1em;
background:none!important;

}

#dle-content span{
background:none!important;
-moz-border-radius: 1em;
}

#site strong{
 visibility:hidden!important;
 display:none!important
}

.slink {
background:none!important;
}

.blocknews .block_text img
{
height:160px;
float:left;
margin:0 10px 10px 5px;
-moz-border-radius: .5em;
-moz-box-shadow: 1px 1px 6px rgba(20,20,20,51);
}

.blocknews .block_text td{
	font-family: sans-serif;
	font-size: 14px;
	line-height: 120%;
	word-spacing: normal;
	letter-spacing: normal;
	text-decoration: none;
	text-transform: none;
	text-align: left;
}
.blocknews .block_text td span{
	color:#3366FF!important;
	text-transform: uppercase;
}

.mainmenu {
background:none!important;
}

