css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {


.new_post_label {
font-size: 14px !important;
margin-right: 0px !important;
margin-top: 2px !important;
letter-spacing: 0px !important;
}

.new_post_label_icon {
background-image: none !important
}

#new_post {
background: #ffffff url('http://i43.tinypic.com/143edd.jpg') top center no-repeat !important;
}


ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_text.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_photo.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_quote.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_link.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_audio.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_video.new_post_label span.new_post_label_icon, ol#posts li#new_post.post.is_mine.with_avatar.new_post a#new_post_label_chat.new_post_label span.new_post_label_icon {

