css:
@namespace url(http://www.w3.org/1999/xhtml);
@namespace dactyl url(http://vimperator.org/namespaces/liberator);

@-moz-document url-prefix(dactyl://help) {

html[dactyl|highlight~="Help"] { 
        background-color: MidnightBlue !important;
}

body[dactyl|highlight~="HelpBody"] { 
	max-width: 85% !important;
        color: #71C671 !important;
        background-color: Black !important;
        padding: .5em 2em !important;
        border-radius: 10px !important;
        box-shadow: 0 0 2px #CC00FF;
        font: 13px/145% "Trebuchet MS", Verdana, Tahoma, Helvetica, "Lucida Grande", "DejaVu Sans", sans-serif !important; 
}

div[dactyl|highlight~="HelpTags"] a[dactyl|highlight~="HelpTag"]{
        color:  #DB8072 !important;
}

div[dactyl|highlight~="HelpSpec"] {
        color: #FF7F36 !important;
        margin-left: -12em !important;
}

div[dactyl|highlight~="HelpOptInfo"] {
        margin-left: 0 !important;
        margin-bottom: 0 !important;
}

span[dactyl|highlight~="HelpDefault"]{
        white-space: nowrap !important;
} 

span[dactyl|highlight~="HelpDefault"] span {
        white-space:pre !important;
}

pre[dactyl|highlight~="HelpCode"], 
html[dactyl|highlight~="Help"] dl {
        background-color: #CAE1FF:  !important;
        border: 1px dashed #4D4D4D  !important;
        color: #420FB !important;
        line-height: 1.1em !important;
        padding: 2px 4px !important;
        border-left: 5px solid #4D4D4D  !important;
        overflow: auto !important;
}

span[dactyl|highlight~="HelpEx"] a {
       font-weight: normal !important;
}

div[dactyl|highlight~="HelpItem"]  {
        margin: 1em 1em 1em 12em !important;
}

div[dactyl|highlight~="HelpDescription"] {
       margin: .5em 0 !important;
}

div[dactyl|highlight~="HelpTOC"]>ol{ 
       border: 1px solid #BBBBBB !important;
       background: #F0F0F0 !important;
       width: 25em !important;
       margin: .5em !important;
       padding-top: 1em !important;
       padding-bottom: 1em !important;
}

div[dactyl|highlight~="HelpTOC"] {
       margin-bottom: 1em !important;
}

html[dactyl|highlight~="Help"] a  {
       color:#CC00FF !important; 
}

html[dactyl|highlight~="Help"] a:visited  {
       color:#1E90FF !important;
       text-decoration: none !important;
}

html[dactyl|highlight~="Help"] a:hover {
       color: red !important;
       background-color:MidnightBlue !important;
       text-decoration: none !important;
}

html[dactyl|highlight~="Help"] h2, h1 {
       padding: .5em 0 !important;
}

