css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashdot.jp") {

/* template v4 beta update 2008/04/14 r2 */

* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}

html {
font-size : 100% !important;
background-color : #fafafa !important;
}

body {
font-size : 100% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #fafafa !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }



#frame
, #frame div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : 100% !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#frame * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#frame a:link { color : #0000ee !important; }
#frame a:visited { color : #551a8b !important; }
#frame a:hover { color : #ee0000 !important; }
#frame a:hover { background-color : transparent !important; }


.copyright
, .btmnav
, #user-section
, #links
, #osdnnavbar
, #slashboxes
, .body .topic
, .bigbanner
{ display : none !important; }

.title h3
, h4
{ padding : 0 !important; }

h4 a
{ margin : 0 !important; }

h4
{ margin-top : 2em !important; }

blockquote
, .quote
{ border-left : 5px solid #666 !important; }

#frame .article .details
, #frame #commentwrap
{ margin : 1em 0 !important; }

#frame .generaltitle
{ margin-top : 1em !important; }
}
